DashboardDefinitionComponents.Configuration:
{
    "entity": "automation",
    "allowBulk": false,
    "dataSource": {
        "load": "function(){            return SkillsWorkflow.executeAPI('GET', 'integration-workflows',        null, null).then(result => {return Promise.resolve(result)        });    }"
    },
    "columns": [
        {
            "dataField": "name",
            "caption": "Name"
        },
        {
            "dataField": "active",
            "caption": "Active"
        },
        {
            "dataField": "id",
            "caption": "Start",
            "cellTemplate": "function(e, options){                           var h = document.createElement('DIV');                    h.setAttribute('class', 'fal fa-play');             e.appendChild(h);                var id = options.data.id;                                h.onclick = function(e) {e.stopPropagation(); debugger;                                             SkillsWorkflow.executeAPI('GET','integration-workflows/'+id+'/execute')                                        }                  }"
        }
    ],
    "onRowClick": "function(e){debugger; if(e.rowType=='data') SW.genericPreview(e.data.id, 'name', 'automation')}",
    "onToolbarPreparing": "function(e) {        var dataGrid = e.component;        e.toolbarOptions.items.unshift({            location: 'after',                       template: 'toggleHeaderFilterButtonTemplate'               });        e.toolbarOptions.items.unshift({            location: 'after',             widget: 'dxButton',             options:{                icon: 'fal fa-times',                onClick: function () {                    SW.genericDelete('integration-workflows', dataGrid, 'id');                }            }        });    }"
}
