    ],
    "onToolbarPreparing": "function(e) { 
        var dataGrid = e.component;  
        e.toolbarOptions.items.unshift({ 
            location: 'after', 
            template: 'toggleHeaderFilterButtonTemplate'
    });
        e.toolbarOptions.items.unshift({ 
            location: 'after', 
            widget: 'dxButton', 
            options: { icon: 'plus', onClick: function() {
                SkillsWorkflow.genericNew('departments');
            }
        }
    });
    var dataGrid = e.component;

        e.toolbarOptions.items.unshift({ 
            location: 'after', 
            widget: 'dxButton', 
            options: { icon: 'fal fa-times', onClick: function() {
                SW.genericDelete('departments', dataGrid);
            }
        }
    });
}"
}

