DocumentsConfiguration:
{
    "entity": "workspace",
    "operations": {
        "read": {
            "endpoint": "dashboardDefinitions"
        },
        "remove": {
            "endpoint": "dashboardDefinitions"
        }
    },
    "layout": {
        "create": {},
        "read": {
            "colCount": 2,
            "items": [
                {
                    "dataField": "Name",
                    "label": {
                        "text": "Name"
                    },
                    "editorType": "dxTextBox",
                    "editorOptions": {
                        "mode": "text",
                        "placeholder": "Workspace name..."
                    },
                    "validationRules": [
                        {
                            "type": "required",
                            "message": "Name is required"
                        }
                    ]
                },
                {
                    "dataField": "DocumentName",
                    "label": {
                        "text": "Document Name"
                    },
                    "editorType": "dxTextBox",
                    "editorOptions": {
                        "mode": "text",
                        "placeholder": "Document name..."
                    }
                },
                {
                    "dataField": "Active",
                    "editorType": "dxCheckBox",
                    "editorOptions": {
                        "mode": "boolean"
                    }
                }
            ]
        }
    },
    "availableOptions": {
        "feed": false,
        "info": true,
        "files": false,
        "userfields": false,
        "dashboard": false
    }
}

DashboardDefinitionComponents.Configuration:
{
    "stateStoring": {
        "enabled": false
    },
    "selection": {
        "mode": "multiple"
    },
    "onRowClick": "function(e){ if(e.rowType=='data')  SW.genericPreview(e.data.Oid, 'Name', 'workspace')}",
    "columns": [
        {
            "alignment": "center",
            "caption": "",
            "dataField": "Oid",
            "width": 30,
            "allowEditing": false,
            "cellTemplate": "function(container, options) {  var h = document.createElement('DIV');    container.appendChild(h);  h.setAttribute('class', 'fal fa-share-square');          h.onclick = function(e) {                e.stopPropagation();          var url = window.location.origin+\"/home/workspaces?id=\" + options.data.Oid;                         window.open(url);                };            }"
        },
        {
            "dataField": "Type",
            "groupIndex": 0,
            "cellTemplate": "function(e, i) {SkillsWorkflow.showDocumentImage(e, 'medium', i.data.key, i.data.key);}",
            "width": 38,
            "groupCellTemplate": "function(e, i) {                                SkillsWorkflow.showDocumentImage(e, 'medium', i.data.key, i.data.key);                  var h1=document.createElement('div');                                   h1.textContent = i.data.key;  h1.style.marginTop = '8px';                                    e.append(h1);                             }"
        },
        {
            "dataField": "Name",
            "width": 200
        },
        {
            "dataField": "Configuration"
        },
        {
            "dataField": "Location",
            "groupCellTemplate": "function(e, i) { if (i.rowType == 'group'){var value = i.value == 0 ? 'Menu' : (i.value == 1 ? 'Document' : 'Configuration'); SW.setAttribute(e, value, 'style', 'text-align: left');}}",
            "groupIndex": 1
        },
        {
            "dataField": "Active",
            "width": 60
        }
    ],
    "onToolbarPreparing": "function (e) {         \n    e.toolbarOptions.items.unshift({             \n        location: 'after',            \n         template: 'toggleHeaderFilterButtonTemplate'   \n              });        var dataGrid = e.component;       \n                 e.toolbarOptions.items.unshift({  \n                                location: 'after',     \n                                        widget: 'dxButton',   \n  options: { icon: 'fal fa-times', onClick: function() {   \n                   SW.genericDelete('workspace', dataGrid);\n                                   }            }        });     }",
    "entity": "workspace"
}
