[
    {
        "WidgetSubTitle": "Billable vs Non-Billable per Type",
        "WidgetType": "chart",
        "WidgetFilterField": "",
        "WidgetColor": "#efefef",
        "WidgetHeight": "500",
        "WidgetHeader": {
            "showContextInfo": false,
            "WidgetPeriodChooser": {
                "type": "year",
                "visible": false
            },
            "WidgetMetrics": {
                "visible": false,
                "Metrics": [
                    {
                        "visible": true,
                        "metricName": "? Forescast",
                        "metricFieldValue": "ForecastTimeGrandTotal"
                    },
                    {
                        "visible": true,
                        "metricName": "? Actual",
                        "metricFieldValue": "ActualTimeGrandTotal"
                    }
                ]
            },
            "showSubTitle": true,
            "showTitle": true
        },
        "WidgetDataSources": {
            "main": {
                "type": "queryText",
                "value": "select sum(iif(eit.[Group] = 0 and jbt.Name = 'Billable', ei.ValueWithVat, 0)) as BillableAdvancedValueWithVat, sum(iif(eit.[Group] = 1 and jbt.Name = 'Billable', ei.ValueWithVat, 0)) as BillableRequestValueWithVat, sum(iif(eit.[Group] = 2 and jbt.Name = 'Billable', ei.ValueWithVat, 0)) as BillableReimbursableValueWithVat, sum(iif(eit.[Group] = 0 and jbt.Name != 'Billable', ei.ValueWithVat, 0)) as UnbillableAdvancedValueWithVat, sum(iif(eit.[Group] = 1 and jbt.Name != 'Billable', ei.ValueWithVat, 0)) as UnbillableRequestValueWithVat, sum(iif(eit.[Group] = 2 and jbt.Name != 'Billable', ei.ValueWithVat, 0)) as UnbillableReimbursableValueWithVat, eit.Name as [ExpenseType] from Expense e, ExpenseItem ei, ExpenseItemType eit, JobType jbt where ei.Expense = e.Oid and ei.[Type] = eit.Oid and jbt.Oid = ei.JobType /*[ExpenseFilter]*/ and e.Oid = @ExpenseId /*[/ExpenseFilter]*/ group by eit.Name",
                "mapping": ""
            }
        },
        "QueryConfiguration": null
    }
]

