DashboardDefinitionComponents.Configuration:
{
  "height":250,
  "columnsAutoWidth": true,
  "columnMinWidth": 50,
  "showColumnHeaders": true,
  "selection": {
    "mode": "none"
  },
  "searchPanel" : {
    "visible" : false
  },
  "groupPanel": {
    "visible": false
  },
  "filterRow": {
    "visible": false
  },
  "export": {
    "enabled": false
  },
  "columnChooser": {
    "enabled": false
  },
  "headerFilter": {
    "visible": false
  },
  "onToolbarPreparing": "",
  "columns": [
    {
      "dataField": "MetricName",
      "caption":"Subject"
    },
    {
      "dataField": "MetricValue",
      "caption":"Value",
      "cellTemplate": "function(element, info) {element.append('<div>' + info.text + '</div>').css('font-weight', 'bold');}",
      "calculateDisplayValue" : "function(r) 
        { 
          if(r.MetricName=='Billable %' || r.MetricName=='Unbillable %') 
            return r.MetricValue * 100; 
          else 
            return r.MetricValue;
       }",
      "cellTemplate": "function(e, i) 
        {
          var h = document.createElement('DIV');
          h.innerHTML = i.text;
          e.appendChild(h);
          if(i.rowIndex == 5 || i.rowIndex == 6) 
            h.style.fontWeight = '600';
        }",
       "format": "#,##0.00"
    }
  ]
}
