{"onClick":"function(e){
    
    var integratorsGrid = SW.getComponent('data');
    var integrator = integratorsGrid.getSelectedRowsData()[0];

    var form = SW.getComponent('formAttendance');
    form.option('disabled', true);
    e.component.option('disabled', true);

    var object = form.getEditor('CompanySelectBox').option('value');
    var companyId = object.CompanyId;
    debugger;

    var externalTableModel = {
        KeyValue: companyId,
        KeyColumnName: integrator.KeyColumnName,
        Columns: [
            {
                ColumnName: 'FtpHostIp',
                Value: object.FtpHostIp
            },
            {
                ColumnName: 'FtpPassword',
                Value: object.FtpPassword
            },
            {
                ColumnName: 'FtpUsername',
                Value: object.FtpUsername
            },
            {
                ColumnName: 'FilePath',
                Value: object.FilePath
            },
            {
                ColumnName: 'AdministratorMail',
                Value: object.AdministratorMail
            },
            {
                ColumnName: 'LeaveTypeName',
                Value: object.LeaveTypeName
            },
            {
                ColumnName: 'IgnoreUserList',
                Value: object.IgnoreUserList
            }
        ]
    };
    SW.executeAPI(
        'PUT',
         'externalTables/'+integrator.TableName+'/rows',
         null,
         JSON.stringify(externalTableModel)
    ).then(
        x => {
            form.option('disabled', false);
            e.component.option('disabled', false);
            SW.Toastr.toastr_Success('Saved');
        }
    ).catch(
        x => {
            form.option('disabled', false);
            e.component.option('disabled', false);
            SW.Toastr.toastr_Success(x);
        }
    )
}","text":"SAVE","icon":"save"}

