{
    "icon": "save",
    "text": "SAVE",
    "onClick": "function(e){
       var editor = SW.getComponent('editor');
    var form = SW.getComponent('form');
    var editorSubWorkflow = SW.getComponent('editorSubWorkflow');

    var actions = JSON.parse(editor.getValue());
    var subWorkflows = JSON.parse(editorSubWorkflow.getValue());
    var formJson = form.component.option('formData');
      var model = {
        name: formJson.name,
        active: formJson.active,
        actions: actions,
        subWorkflows: subWorkflows,
        scheduler: formJson.scheduler
    }
    SW.executeAPI('PUT', 'integration-workflows/'+formJson.id, null, model)
    .then( x => {
        SW.Toastr.toastr_Success("Saved");
    })
    .catch( x => {
        SW.Toastr.toastr_Error(x);
    })
}"
}

