{"onClick":"function(e){
    var buttonGroup = SW.getComponent('buttonGroup');
    var operation = buttonGroup.option('selectedItems')[0];
    if(operation.text == 'Apply'){
        Apply(e);
    } else {
        Delete(e);
    }

    function Apply(e) {
        SetLoading();
        var gridUsers = SW.getComponent('data');
        var gridLeaveTypes = SW.getComponent('applyLeaveTypesGrid');
        if (gridUsers && gridLeaveTypes) {
            var selectedUsers = gridUsers.getSelectedRowKeys();
            var selectedLeaveTypes = gridLeaveTypes.getSelectedRowKeys();
        }
        var year = SW.getComponent('formLTyear') != null ? SW.getComponent('formLTyear').option('value') : null;
        var days = SW.getComponent('formLTdays') != null ? SW.getComponent('formLTdays').option('value') : null;
        var startDate = SW.getComponent('formLTstartDate') != null ? SW.getComponent('formLTstartDate').option('value') : null;
        var expirationDate = SW.getComponent('formLTexpirationDate') != null ? SW.getComponent('formLTexpirationDate').option('value') : null;
        var seniority = false;
        seniority = SW.getComponent('formLTseniority').option('value') != null ? SW.getComponent('formLTseniority').option('value') : false;
        var carryForward = false;
        carryForward = SW.getComponent('formLTcarryForward').option('value') != null ? SW.getComponent('formLTcarryForward').option('value') : false;
        if (!selectedUsers || !selectedLeaveTypes || !year || !days || !startDate || !expirationDate) {
            StopLoading();
            return SW.Toastr.toastr_Error('A field is missing');
        }
        if (selectedUsers.length == 0) {
            StopLoading();
            return SW.Toastr.toastr_Error('No users selected');
        }
        if (selectedLeaveTypes.length == 0) {
            StopLoading();
            return SW.Toastr.toastr_Error('No leave types selected');
        }
        var selectedUsersIds = [];
        selectedUsers.forEach(
            user => {
                selectedUsersIds.push(user.UserId);
            }
        );
        var selectedLeaveTypesIds = [];
        selectedLeaveTypes.forEach(
            leaveType => {
                selectedLeaveTypesIds.push(leaveType.Oid);
            }
        );
        var details = {
            Year: year,
            TotalVacationDays: days,
            StartDate: startDate,
            ExpirationDate: expirationDate,
            Seniority: seniority,
            CarryForward: carryForward
        };
        var batchModel = {
            LeaveInfoBatchAction: 0,
            UsersIdList: selectedUsersIds,
            LeaveTypesIdList: selectedLeaveTypesIds,
            Details: details
        };
        SW.executeAPI('POST', 'leaveInfos/batch', null, batchModel).then(x => {
            SW.Toastr.toastr_Success('Applied');
            StopLoading();
        }).catch(x => {
            StopLoading();
        });
    }

    function Delete(e){
        SetLoading();
        var gridUsers = SW.getComponent('data');
        var gridLeaveTypes = SW.getComponent('applyLeaveTypesGrid');
        if (gridUsers && gridLeaveTypes) {
            var selectedUsers = gridUsers.getSelectedRowKeys();
            var selectedLeaveTypes = gridLeaveTypes.getSelectedRowKeys();
        };
        if (selectedUsers.length == 0) {
            StopLoading();
            return SW.Toastr.toastr_Error('No users selected');
        }
        if (selectedLeaveTypes.length == 0) {
            StopLoading();
            return SW.Toastr.toastr_Error('No leave types selected');
        }
        var selectedUsersIds = [];
        selectedUsers.forEach(
            user => {
                selectedUsersIds.push(user.UserId);
            }
        );
        var selectedLeaveTypesIds = [];
        selectedLeaveTypes.forEach(
            leaveType => {
                selectedLeaveTypesIds.push(leaveType.Oid);
            }
        );
        var batchModel = {
            LeaveInfoBatchAction: 1,
            UsersIdList: selectedUsersIds,
            LeaveTypesIdList: selectedLeaveTypesIds
        };
        SW.executeAPI('POST', 'leaveInfos/batch', null, batchModel).then(x => {
            SW.Toastr.toastr_Success('Deleted');
            StopLoading();
        }).catch(x=>{
            StopLoading();
        });
    }

    function SetLoading() {
        var grid = SW.getComponent('applyLeaveTypesGrid');
        grid.option('disabled', true);
        var form = SW.getComponent('form');
        form.option('disabled', true);
        var buttonGroup = SW.getComponent('buttonGroup');
        buttonGroup.option('disabled', true);
        debugger;
    }

    function StopLoading() {
        var grid = SW.getComponent('applyLeaveTypesGrid');
        grid.option('disabled', false);
        var form = SW.getComponent('form');
        form.option('disabled', false);
        var buttonGroup = SW.getComponent('buttonGroup');
        buttonGroup.option('disabled', false);
    }
