{
    "height": "400",
    "onRowClick": "function(e){if(e.rowType=='data') SkillsWorkflow.preview(e.key, 'job');}",
    "keyExpr": "Oid",
    "selection": {
        "mode": "single"
    },
    "allowFiltering": true,
    "searchPanel": {
        "visible": true
    },
    "scrolling": {
        "mode": "virtual"
    },
    "columnMinWidth": "50",
    "grouping": {
        "autoExpandAll": true
    },
    "columns": [
        {
            "dataField": "Job",
            "name": "Jobs",
            "caption": "Jobs",
            "width": 500
        },
        {
            "dataField": "JobType",
            "name": "Job Type",
            "caption": "Job Type"
        },
        {
            "dataField": "Client",
            "name": "Client",
            "caption": "Client",
            "width": 100,
            "groupIndex": 1,
            "groupCellTemplate": "function groupCell(e, i) { var h1=document.createElement('span');                  var h=document.createElement('span');                    e.append(h1);                       var data='';                    if(i.data.items != null){ data = i.data.items[0]}                     else { data= i.data.collapsedItems[0]            }                                        SW.showImage(h1, 'client', 'small', data.ClientHasImage, data.Client, data.ClientId);                      e.append(h);                                              var ht= ' ' + i.text + ' ('+i.summaryItems[0].value+')';                                          h.innerHTML = ht;        }",
            "cellTemplate": "function cellTemplate(e, i) { var h1=document.createElement('span');                  var h=document.createElement('span');                    e.append(h1);                               SW.showImage(h1, 'client', 'small', i.data.ClientHasImage, i.data.ClientName, i.data.ClientId);                      e.append(h);                                              var ht= ' ' + i.text + ' ';                                          h.innerHTML = ht;    }"
        },
        {
            "dataField": "LastActivity",
            "caption": "Last Activity",
            "alignment": "center",
            "format": "#,##0",
            "width": 100,
            "cellTemplate": "function(e, i) {SK.setRAG(e, i.value, i.value>=15, i.value>=5 && i.value<15, i.value<5);}"
        },
        {
            "dataField": "Days",
            "caption": "Days",
            "alignment": "center",
            "format": "#,##0",
            "width": 70,
            "cellTemplate": "function(e, i) {            var h = document.createElement('DIV');             e.appendChild(h);            h.innerHTML = i.text;             h.style['color'    ] = '#ffffff';             h.style['min-width'    ] = '30px';            h.style['border-radius'    ] = '2px';             h.style['padding'    ] = '3px 7px';             h.style['font-weight'    ] = '700';            h.style['white-space'    ] = 'nowrap';            h.style['text-align'    ] = 'center';             h.style['display'    ] = 'inline-block';            if(i.value<0)             {                h.style['background-color'      ] = '#f5564a';    };            if(i.value>=2)             {                h.style['background-color'      ] = '#7ec880';    };            if(i.value==0 || i.value==1)            {                h.style['background-color'      ] = '#ffc720';    };  }"
        },
        {
            "dataField": "Stage",
            "caption": "Stage",
            "name": "Name",
            "alignment": "left",
            "format": "#,##0",
            "width": 80,
            "groupCellTemplate": "function(e, i) {    var color=i.component.option().dataSource.find(row=>row.Stage==i.text).Color;        var h=document.createElement('span');        h.style.backgroundColor = color;         h.classList.add('documentStageColor');        e.append(h);        h=document.createElement('span');        var ht=' ' + i.text + ' ('+i.summaryItems[    0  ].value+')';         h.innerHTML = ht;        e.append(h);}",
            "groupIndex": 0
        },
        {
            "dataField": "Enddate",
            "caption": "End date",
            "alignment": "right",
            "dataType": "date",
            "format": "d MMM",
            "width": 100
        },
        {
            "dataField": "JobClassification",
            "caption": "Classification",
            "alignment": "left",
            "format": "#,##0"
        },
        {
            "dataField": "Executor",
            "caption": "Executor",
            "alignment": "right",
            "format": "#,##0",
            "width": 100,
            "cellTemplate": "function(e, i) {          if(i.text == '') return;                     var executors = i.text.split(',');     function myFunction(item, index) {  SW.showImage(e, 'user','small', true, null, item);                   }          executors.forEach( (item, index) => myFunction(item, index));}"
        }
    ],
    "sortByGroupSummaryInfo": [
        {
            "summaryItem": "count"
        }
    ],
    "summary": {
        "groupItems": [
            {
                "column": "Jobs",
                "summaryType": "count",
                "valueFormat": "#,##0",
                "displayFormat": "{0}",
                "alignment": "left"
            }
        ],
        "totalItems": [
            {
                "column": "Effort",
                "summaryType": "sum",
                "valueFormat": "#,##0",
                "displayFormat": "{0}",
                "alignment": "right",
                "cellTemplate": "function(e, i) {          var h = document.createElement('DIV');           e.appendChild(h);          h.innerHTML = i.text;           h.style['color'  ] = '#ffffff';           if(i.value<0) {              h.style['background-color'    ] = '#f5564a';  }}"
            }
        ]
    }
}
