[
    {
        "WidgetSubTitle": "Actual Time",
        "WidgetType": "chart",
        "WidgetFilterField": "",
        "WidgetColor": "#efefef",
        "WidgetHeight": 100,
        "WidgetHeader": {
            "showContextInfo": false,
            "WidgetPeriodChooser": {
                "type": "year",
                "visible": false
            },
            "WidgetMetrics": {
                "visible": true,
                "Metrics": [
                    {
                        "visible": true,
                        "metricName": "# Actual (Billable)",
                        "metricFieldValue": "DirectHoursGrandTotal"
                    },
                    {
                        "visible": true,
                        "metricName": "# Actual (Non Billable)",
                        "metricFieldValue": "NewBusinessHoursGrandTotal"
                    }
                ]
            },
            "showSubTitle": true,
            "showTitle": true
        },
        "WidgetDataSources": {
            "main": {
                "type": "querytext",
                "value": "select [TOP_MACRO] v.*,\r\n                       sum(DirectHours) over (order by v.DepartmentId) as DirectHoursRunningTotal,\r\n                       sum(DirectHours) over () as DirectHoursGrandTotal,\r\n                       sum(NewBusinessHours) over (order by v.DepartmentId) as NewBusinessHoursRunningTotal,\r\n                       sum(NewBusinessHours) over () as NewBusinessHoursGrandTotal,\r\n                       sum(AdministrativeHours) over (order by v.DepartmentId) as AdministrativeHoursRunningTotal,\r\n                       sum(AdministrativeHours) over () as AdministrativeHoursGrandTotal,\r\n                       sum(TimeOffHours) over (order by v.DepartmentId) as TimeOffHoursRunningTotal,\r\n                       sum(TimeOffHours) over () as TimeOffHoursGrandTotal,\r\n                       sum(TotalHours) over () as GlobalTotalHours\r\n                    from\r\n                    (\r\n                       select t.[DepartmentId],\r\n                             t.[Department] as DepartmentName,\r\n                             isnull(t.DirectHours, 0.0) as DirectHours,\r\n                             isnull(t.NewBusinessHours, 0.0) as NewBusinessHours,\r\n                             isnull(t.AdministrativeHours, 0.0) as AdministrativeHours,\r\n                             isnull(uv.TimeOffHours, 0.0) as TimeOffHours,\r\n                             isnull(t.[Hours], 0.0) + isnull(uv.TimeOffHours, 0.0) as TotalHours\r\n                       from\r\n                       (\r\n                          select t.[DepartmentId],\r\n                                t.[Department],\r\n                                sum(t.[Hours]) as [Hours],\r\n                                (sum(t.[Hours]) - (sum(t.NewBusinessHours) + sum(t.AdministrativeHours))) as DirectHours,\r\n                                sum(t.NewBusinessHours) as NewBusinessHours,\r\n                                sum(AdministrativeHours) as AdministrativeHours\r\n                          from   SkillAnalyticsTimeUtilization t\r\n                          where\t 1 = 1\r\n\t\t\t\t\t\t/*[ProjectFilter]*/ and t.ProjectId = @ProjectId /*[/ProjectFilter]*/\r\n                          group by t.[DepartmentId],\r\n                                t.[Department]\r\n                       ) t\r\n                       left outer join \r\n                       (\r\n                          select uv.[DepartmentId],\r\n                                uv.[Department],\r\n                                sum(uv.[Hours]) as TimeOffHours\r\n                          from   SkillAnalyticsLeaves uv\r\n\t                      where\t 1 = 1\r\n                          group by uv.[DepartmentId],\r\n                                uv.[Department]\r\n                       )\r\n                       uv on (uv.[DepartmentId] = t.DepartmentId)\r\n                    ) v"
            }
        },
        "Filters": {},
        "QueryConfiguration": {
            "PreviousPeriod": {
                "Show": false,
                "KeyFieldName": "",
                "Type": 0
            },
            "RequiredFilters": []
        }
    }
]

