  "summary": {
    "calculateCustomSummary": "function(options) {
        if(options.name == 'ActualPlannedPercentageSummary') {
            switch(options.summaryProcess) {
                case 'start':
                  options.totalValue = [0, 0];
                  break;
                case 'calculate':
                  options.totalValue[0] = options.totalValue[0] + options.value.ActualHours;
                  options.totalValue[1] = options.totalValue[1] + options.value.PlannedHours;
                  break;
                case 'finalize':
                  if(Number(options.totalValue[1]) > 0)
                    options.totalValue = options.totalValue[0] / options.totalValue[1];
                  else
                    options.totalValue = 0;
                  break;
            }
        }
