grid.mapper:
function mapper(response, entityId){
    if (response.ProjectCompany)
    {
        return {
            AssociationId: response.ProjectCompany.Id,
            Id: response.ProjectCompany.CompanyId,
            Name: response.ProjectCompany.CompanyName,
            ImageId: response.ProjectCompany.CompanyId,
            ImageName: response.ProjectCompany.CompanyName,
            Info1: null,
            Info2: null,
        }
    }
    else
    {
        return {
            AssociationId: response.Id,
            Id: response.CompanyId,
            Name: response.CompanyName,
            ImageId: response.CompanyId,
            ImageName: response.CompanyName,
            Info1: null,
            Info2: null,
        }
    }
}

