{"text":"RESET","onClick":"function(e){
    e.component.option('disabled', true);
    var form = SW.getComponent('formPassword');
    form.option('disabled', true);
    var id = SW.getComponent('data').getSelectedRowKeys()[0].UserId;
    var pass = SW.getComponent('newPass').option('value'); 
    var model = {
        NewPassword: pass
    };
    SW.executeAPI("POST", "v3/users/" + id + "/reset-password", null, model)
    .then(
        x => {
            form.option('disabled', false);
            SW.Toastr.toastr_Success("Done");
            e.component.option('disabled', false);
        }
    )
    .catch( 
        x => {
            form.option('disabled', false);
            e.component.option('disabled', false);
        }
    );
}"}
