{
    "onContentReady": "function bla(e) {\r\n    var queryResult = e.component.getDataSource();        \r\n    if (typeof queryResult === 'undefined') return;        \r\n    if (this.completed) return;        \r\n    var objectResult = {};\r\n        queryResult._items.forEach( row => {\r\n            let inTime =  'InTime' + row.WeekDay;\r\n            let outTime = 'OutTime' + row.WeekDay;\r\n            let inAdjusted = 'InAdjusted' + row.WeekDay;\r\n            let outAdjusted = 'OutAdjusted' + row.WeekDay;\r\n            let underTime = 'UnderTime' + row.WeekDay;\r\n            let timesheet = 'Timesheet' + row.WeekDay;\r\n            let leave = 'Leave' + row.WeekDay;\r\n            let tardiness = 'Tardiness' + row.WeekDay;\r\n            let inTimeValue = { [inTime]: row.InTime };\r\n            let outTimeValue = { [outTime]: row.OutTime };\r\n            let inAdjustedValue = { [inAdjusted]: row.InAdjusted };\r\n            let outAdjustedValue = { [outAdjusted]: row.OutAdjusted };\r\n            let undertimeValue = { [underTime]: row.UnderTime };\r\n            let timesheetValue = { [timesheet]: row.Timesheet };\r\n            let leaveValue = { [leave]: row.Leave };\r\n            let tardinessValue = { [tardiness]: row.Tardiness };\r\n            if(!objectResult[row.Oid])\r\n                objectResult[row.Oid] = {\r\n                    DepartmentName: row.DepartmentName,\r\n                    UserTypologyGroup: row.UserTypologyGroup,\r\n                    HasImage: row.HasImage,\r\n                    Oid: row.Oid,\r\n                    Name: row.Name,\r\n                    ExternalId: row.ExternalId,\r\n                    TotalTardiness: row.TotalTardiness,\r\n                    TotalTardinessPlusTotalUnderTime: row.TotalTardinessPlusTotalUnderTime,\r\n                    [inTime]: row.InTime,\r\n                    [outTime]: row.OutTime,\r\n                    [inAdjusted]: row.InAdjusted,\r\n                    [outAdjusted]: row.OutAdjusted,\r\n                    [underTime]: row.UnderTime,\r\n                    [timesheet]: row.Timesheet,\r\n                    [leave]: row.Leave,\r\n                    [tardiness]: row.Tardiness\r\n                };\r\n            else {\r\n                objectResult[row.Oid] = {\r\n                    ...objectResult[row.DepartmentName],\r\n                    ...objectResult[row.UserTypologyGroup],\r\n                    ...objectResult[row.HasImage],\r\n                    ...objectResult[row.Oid],\r\n                    ...objectResult[row.Name],\r\n                    ...objectResult[row.ExternalId],\r\n                    ...objectResult[row.TotalTardiness],\r\n                    ...objectResult[row.TotalTardinessPlusTotalUnderTime],\r\n                    ...inTimeValue,\r\n                    ...outTimeValue,\r\n                    ...inAdjustedValue,\r\n                    ...outAdjustedValue,\r\n                    ...undertimeValue,\r\n                    ...timesheetValue,\r\n                    ...leaveValue,\r\n                    ...tardinessValue\r\n                }\r\n            }\r\n        });\r\n        var arrayResult = [];\r\n        Object.values(objectResult).forEach((value) => arrayResult.push(value));\r\n        e.component.instance().beginUpdate();\r\n        e.component.option('dataSource', arrayResult);\r\n        e.component.instance().columnOption(\"DepartmentName\", \"groupIndex\", 0);\r\n        e.component.instance().endUpdate();\r\n        this.completed = true;\r\n        \r\n}",
