[
    {
        "WidgetSubTitle": "Closed and In Progress Tickets",
        "WidgetType": "rangeselector",
        "WidgetFilterField": "",
        "WidgetColor": "#efefef",
        "WidgetXPosition": 0,
        "WidgetYPosition": 2,
        "WidgetRows": 4,
        "WidgetColumns": 48,
        "WidgetHeader": {
            "WidgetPeriodChooser": {
                "startDate": "2019-01-01",
                "endDate": "2019-12-31",
                "type": "year",
                "visible": false
            },
            "showSubTitle": false,
            "showTitle": false
        },
        "WidgetDataSources": {
            "main": {
                "type": "querytext",
                "value": "SELECT  SUM(v.[Hours]) as [Hours],\r\n        v.[Month] \r\nFROM\r\n(\r\n        SELECT  SUM(v.ActualTime) as [Hours],\r\n                DATEFROMPARTS(v.[Year], v.[Month], 1) as [Month]\r\n        FROM    Analytics.Timesheet(@CurrentUserId) v\r\n        WHERE\t(v.CompanyId = @CompanyId or @CompanyId = '00000000-0000-0000-0000-000000000000') and\r\n                v.Year = @Year\r\n        GROUP BY DATEFROMPARTS(v.[Year], v.[Month], 1)\r\n        UNION ALL\r\n        SELECT  SUM(v.[Hours]) as [Hours],\r\n                DATEFROMPARTS(v.[Year], v.[Month], 1) as [Month]\r\n        FROM    Analytics.Leave(@CurrentUserId) v\r\n        WHERE\t(v.CompanyId = @CompanyId or @CompanyId = '00000000-0000-0000-0000-000000000000') and\r\n                v.Year = @Year\r\n        GROUP BY DATEFROMPARTS(v.[Year], v.[Month], 1)\r\n        UNION ALL\r\n        SELECT  SUM(v.[Hours]) as [Hours],\r\n                DATEFROMPARTS(v.[Year], v.[Month], 1) as [Month]\r\n        FROM    Analytics.Holiday(@CurrentUserId) v\r\n        WHERE\t(v.CompanyId = @CompanyId or @CompanyId = '00000000-0000-0000-0000-000000000000') and\r\n                v.Year = @Year\r\n        GROUP BY DATEFROMPARTS(v.[Year], v.[Month], 1)\r\n) v\r\nGROUP BY v.[Month]\r\n",
                "mapping": ""
            }
        },
        "Filters": {},
        "QueryConfiguration": {
            "RequiredFilters": [
                "Year",
                "Company"
            ]
        }
    }
]

