[
    {
        "WidgetSubTitle": "Comparison between Under Development & Scheduled for Development",
        "WidgetType": "pie",
        "WidgetFilterField": "",
        "WidgetColor": "",
        "WidgetHeight": "",
        "WidgetHeader": {
            "showContextInfo": false,
            "WidgetPeriodChooser": {
                "type": "year",
                "visible": false
            },
            "WidgetMetrics": {
                "visible": true,
                "Metrics": [
                    {
                        "visible": true,
                        "metricName": "# Delayed",
                        "metricFieldValue": "DelayedGrandTotal"
                    }
                ]
            },
            "showSubTitle": true,
            "showTitle": true
        },
        "WidgetDataSources": {
            "main": {
                "type": "queryText",
                "value": "select ws.name as Stage, count(*) as Total from SkillJob j, WorkflowState ws, WorkflowStateType wst where ws.Oid = j.WorkflowStateId and wst.Oid = j.WorkflowStateTypeId and ws.name in ('Under Development', 'Scheduled for Development') /*[DateFilter]*/ and j.AgreedDate >= @FromDate and j.AgreedDate <= @ToDate /*[/DateFilter]*/ group by ws.Name"
            }
        },
        "QueryConfiguration": null
    }
]

