DashboardDefinitionComponents.Parameters:
[
    {
        "WidgetSubTitle": "Actual Time - Select Months",
        "WidgetType": "rangeSelector",
        "WidgetFilterField": "",
        "WidgetColor": "#e0e0e0",
        "WidgetHeight": "100",
        "WidgetHeader": {
            "showContextInfo": false,
            "WidgetPeriodChooser": {
                "startDate": "2018/01/01",
                "endDate": "2019/06/30",
                "type": "year",
                "visible": false
            },
            "showSubTitle": true,
            "showTitle": true
        },
        "WidgetDataSources": {
            "main": {
                "type": "queryText",
                "value": "select max(datepart(ww, j.AgreedDate)) as week, min(j.AgreedDate) as WeekStart, sum(iif(ws.name = 'Scheduled for Development', 1, 0)) as ScheduledForDevelopment, sum(iif(ws.name = 'Under Development', 1, 0)) as UnderDevelopment from SkillJob j, WorkflowState ws, WorkflowStateType wst where ws.Oid = j.WorkflowStateId and wst.Oid = j.WorkflowStateTypeId and ws.name in ('Under Development', 'Scheduled for Development') and j.AgreedDate >= @FromDate and j.AgreedDate <= @ToDate group by datepart(yyyy, j.AgreedDate), datepart(ww, j.AgreedDate) order by WeekStart"
            }
        },
        "QueryConfiguration": null
    }
]

[
    {
        "WidgetSubTitle": "Job's Delayed or On Time by status",
        "WidgetType": "grid",
        "WidgetFilterField": "",
        "WidgetColor": "",
        "WidgetHeight": "300",
        "WidgetHeader": {
            "showContextInfo": false,
            "WidgetPeriodChooser": {
                "type": "year",
                "visible": false
            },
            "WidgetMetrics": {
                "visible": true,
                "Metrics": [
                    {
                        "visible": true,
                        "metricName": "# Delayed Under Development",
                        "metricFieldValue": "DelayedUnderDevelopmentJobsGrandTotal"
                    }
                ]
            },
            "showSubTitle": true,
            "showTitle": true
        },
        "WidgetDataSources": {
            "main": {
                "type": "queryText",
                "value": "select j.effort, j.Job, ws.Name as Stage, j.Project, j.AgreedDate as Enddate, j.Oid, ISNULL(ws.Color, wst.Color) as Color, DATEDIFF(day,getdate(),AgreedDate) as Days, sum(iif(ws.name = 'Under Development' and AgreedDate < getdate(), 1, 0)) over() as DelayedUnderDevelopmentJobsGrandTotal, (select min(ass.[User]) from Assignment ass, AssignmentType ast where ass.DocumentOid = j.Oid and ast.Name = 'Executor' and ass.AssignmentType = ast.Oid) as Executor from SkillJob j, WorkflowState ws,WorkflowStateType wst where ws.Oid = j.WorkflowStateId and wst.Oid = j.WorkflowStateTypeId and ws.name in ('Under Development', 'Scheduled for Development') /*[DateFilter]*/ and j.AgreedDate >= @FromDate and j.AgreedDate <= @ToDate /*[/DateFilter]*/"
            }
        },
        "QueryConfiguration": {}
    }
]

