[
    {
        "WidgetSubTitle": "Comparison between On Hold, Waiting & In Progress",
        "WidgetType": "funnel",
        "WidgetFilterField": "",
        "WidgetColor": "",
        "WidgetHeight": "",
        "WidgetHeader": {
            "showContextInfo": false,
            "WidgetPeriodChooser": {
                "type": "year",
                "visible": false
            },
            "WidgetMetrics": {
                "visible": false,
                "Metrics": [
                    {
                        "visible": true,
                        "metricName": "# On Hold",
                        "metricFieldValue": "OnHoldGrandTotal"
                    },
                    {
                        "visible": true,
                        "metricName": "# Waiting",
                        "metricFieldValue": "WaitingGrandTotal"
                    },
                    {
                        "visible": true,
                        "metricName": "# In Progress",
                        "metricFieldValue": "OnGoingGrandTotal"
                    }
                ]
            },
            "showSubTitle": true,
            "showTitle": true
        },
        "WidgetDataSources": {
            "main": {
                "type": "queryText",
                "value": "SELECT (SUM(SUM(IIF(v.IsDelayed = 1, 1, 0))) OVER ()) AS DelayedGrandTotal ,(SUM(SUM(IIF(v.IsWaiting = 1, 1, 0))) OVER ()) AS WaitingGrandTotal ,(SUM(SUM(IIF(v.IsOnHold = 1, 1, 0))) OVER ()) AS OnHoldGrandTotal ,(SUM(SUM(IIF(v.IsOnHold = 0 AND v.IsWaiting = 0, 1, 0))) OVER ()) AS OnGoingGrandTotal FROM (SELECT j.Oid AS JobId ,j.StateId ,j.StateTypeId ,j.[Date] ,IIF(j.[Date] < GETDATE() AND j.StateTypeCode != 6 AND ds.[Status] NOT IN (5, 4) AND ws.Delayed = 1, 1, 0) IsDelayed ,IIF(j.StateTypeCode IN (8, 5, 21), 1, 0) IsWaiting ,IIF(j.StateTypeCode IN (14, 17, 18, 13), 1, 0) IsOnHold FROM Analytics.Job(@CurrentUserId) j ,Deliverable d ,DocumentState ds ,WorkflowState ws WHERE 1 = 1 /*[DateFilter]*/ AND CAST(j.[Date] AS DATE) BETWEEN @FromDate AND @ToDate /*[/DateFilter]*/ AND d.Oid = j.Oid AND ds.Oid = d.DocumentState AND ws.Oid = j.StateId) v",
                "mapping": ""
            }
        },
        "QueryConfiguration": null
    }
]

