    "barMargin": 10,
    "columns": [
        {
            "text": "Resources",
            "field": "name",
            "width": 120,
            "region": "left",
            "htmlEncode": false,
            "renderer": "function renderer(_ref){ if(!_ref.record.hasImage){ return `<div style=' display: flex; align-items: center; justify-content: space-between; '> <div style='border-radius: 250px; overflow: hidden; width: 26px; height: 26px; border: none;'> <div style='color: rgb(42, 128, 185); background-color: rgb(197, 219, 243); font-size: 14px; height: 100%; line-height: 22px; display: flex; align-items: center; justify-content: center'> <span style='text-align: center;'> ${_ref.record.name[0]} </span> </div> </div> <div style='font: 11px \"Varela Round\";    color: #757575;width: calc(100% - 32px);'>Unassigned</div></div>`; }    if(_ref.record.hasImage){return `<div style='display: flex;width: 100%;align-items: center;justify-content: space-between;'> <img style='border-radius: 20px; height: 26px;' src=${SkillsWorkflow.getUserImages([_ref.record.id])}/> <div style='width: calc(100% - 32px); font: 11px \"Varela Round\";    color: #757575;'> <div style='overflow: hidden;text-overflow: ellipsis;white-space: nowrap;'>${_ref.record.name}</div> <div style='overflow: hidden;text-overflow: ellipsis;white-space: nowrap;'>${_ref.record.typology}</div> </div> </div>`;}}"
        }
    ],
    "eventStyle": "border",
    "eventColor": "yellow",
    "eventRenderer": "function({resourceRecord, eventRecord, assignmentRecord, tplData}) { return `<div style='width: 100%; cursor: pointer; border-radius: 4px; height: 34px; background-color: ${eventRecord.eventColor}; color: white;display: flex; justify-content: space-around; align-items: center;'><img style='border-radius: 20px; height: 26px;' src='${SkillsWorkflow.getClientImages([assignmentRecord.get('clientId')])}'><div style='width: calc( 100% - 36px); display: flex; align-items: center; justify-content: space-between;'><div style='width: 75%'><div style='width: 100%; margin-top: 3px; font-size: 12px; font-weight: normal; width: 100%; overflow: hidden; text-overflow: ellipsis; min-height: 17px;'>${eventRecord.name}</div><div style='margin-top: -6px; font-size: 10px; font-weight: normal; width: 100%; overflow: hidden; text-overflow: ellipsis; font-style: italic'>${assignmentRecord.projectName}</div></div><div style='width: 20%;font-size: 12px; margin: auto; text-align: right'><div style='height: 14px;'><i class='fal fa-clock' style='color: #f44336; font-weight: 600; border-radius: 50px;font-size: 12px; background-color: white;'></i></div><div id='duration'>23d</div></div></div</div>`;}",
    "features": {
        "columnLines": true,
        "eventResize": true,
        "eventEdit": false
    },
    "listeners": {
        "eventdblclick": "function({ scheduler, eventRecord, event}){ SkillsWorkflow.preview(eventRecord.id, 'job') }"
    }
}

