DashboardDefinitionComponents.Parameters:
[
    {
        "WidgetSubTitle": "Closed and In Progress Tickets",
        "WidgetType": "rangeSelector",
        "WidgetFilterField": "",
        "WidgetColor": "#e0e0e0",
        "WidgetHeight": "250",
        "WidgetHeader": {
            "WidgetPeriodChooser": {
                "startDate": "2019/04/01",
                "endDate": "2019/07/30",
                "type": "year",
                "visible": false
            },
            "WidgetMetrics": {
                "visible": true,
                "Metrics": [
                    {
                        "visible": true,
                        "metricName": "# Done",
                        "metricFieldValue": "Done"
                    },
                    {
                        "visible": true,
                        "metricName": "# To Do",
                        "metricFieldValue": "ToDo"
                    }
                ]
            },
            "showSubTitle": true,
            "showTitle": true
        },
        "WidgetDataSources": {
            "main": {
                "type": "queryText",
                "value": "select max(datepart(ww, j.AgreedDateUtc)) as week, cast(min(j.AgreedDateUtc) as date) as WeekStart, sum(iif(ws.name in ('Done', 'Closed','Closed (unrelated)' ,'Canceled', 'On Hold'), 1, 0)) as Done, sum(iif(ws.name not in ('Done', 'Closed','Closed (unrelated)' ,'Canceled', 'On Hold','Scheduled for Development'), 1, 0)) as ToDo from    Deliverable j, WorkflowState ws, WorkflowStateType wst, Department dep where   ws.Oid = j.WorkflowState and wst.Oid = ws.WorkflowStateType and dep.Oid = j.Department and exists ( select 1 from Assignment ass, AssignmentType ast where ass.[User] = '34c14c76-fe0e-45c5-ac88-e30ce0824e8d' and ass.DocumentOid = j.Oid and ass.AssignmentType = ast.Oid and ast.Name = 'Executor' ) /*[DateFilter]*/ and cast(j.AgreedDateUtc as Date) >= @FromDate and cast(j.AgreedDateUtc as Date) < @ToDate/*[/DateFilter]*/ group by datepart(yyyy, j.AgreedDateUtc), datepart(ww, j.AgreedDateUtc) order by WeekStart"
            }
        },
        "QueryConfiguration": {
            "RequiredFilters": [
                "startDate",
                "endDate"
            ]
        }
    }
]

[
    {
        "WidgetSubTitle": "Tickets in the Selected Period",
        "WidgetType": "grid",
        "WidgetFilterField": "",
        "WidgetColor": "",
        "WidgetHeight": "",
        "WidgetHeader": {
            "showContextInfo": false,
            "WidgetPeriodChooser": {
                "type": "year",
                "visible": false
            },
            "WidgetMetrics": {
                "visible": true,
                "Metrics": [
                    {
                        "visible": true,
                        "metricName": "# Work To Do",
                        "metricFieldValue": "WorkGrandTotal"
                    },
                    {
                        "visible": true,
                        "metricName": "# Delayed To Do",
                        "metricFieldValue": "DelayedToDoJobsGrandTotal"
                    },
                    {
                        "visible": true,
                        "metricName": "# Delayed Work in Progress",
                        "metricFieldValue": "DelayedWorkInProgressJobsGrandTotal"
                    }
                ]
            },
            "showSubTitle": true,
            "showTitle": true
        },
        "WidgetDataSources": {
            "main": {
                "type": "queryText",
                "value": "select j.effort, j.NameAndExternalId as Job, ws.Name as Stage, j.Project, j.AgreedDateUtc as Enddate, j.Oid, j.Priority as Priority, isnull(ws.Color, wst.Color) as Color, datediff(day,getdate(),AgreedDateUtc) as Days, DATEDIFF(day,ModifiedOn,getdate()) as LastActivity, sum(iif(ws.name = 'To Do' and CONVERT(char(10), AgreedDateUtc,126)< CONVERT(char(10), GetDate(),126), 1, 0)) over() as DelayedToDoJobsGrandTotal, sum(iif(ws.name = 'Work in Progress' and CONVERT(char(10), AgreedDateUtc,126)< CONVERT(char(10), GetDate(),126), 1, 0)) over() as DelayedWorkInProgressJobsGrandTotal, sum(iif(ws.name not in ('Draft','Done', 'Closed','Closed (unrelated)' ,'Canceled', 'On Hold', 'Scheduled for Development'),1,0)) over() as WorkGrandTotal, (select min(ass.[User]) from Assignment ass, AssignmentType ast where ass.DocumentOid = j.Oid and ast.Name = 'Executor' and ass.AssignmentType = ast.Oid) as Executor from Deliverable j, WorkflowState ws, WorkflowStateType wst, Department dep where ws.Oid = j.WorkflowState and wst.Oid = ws.WorkflowStateType and dep.Oid = j.Department and ws.name not in ('Draft','Done', 'Closed','Closed (unrelated)' ,'Canceled', 'On Hold', 'Scheduled for Development', 'Under Approval', 'Request Approval') and exists ( select 1 from Assignment ass, AssignmentType ast where ass.[User] = '34c14c76-fe0e-45c5-ac88-e30ce0824e8d' and ass.DocumentOid = j.Oid and ass.AssignmentType = ast.Oid and ast.Name = 'Executor' ) /*[DateFilter]*/ and j.AgreedDateUtc >= @FromDate and j.AgreedDateUtc <= @ToDate /*[/DateFilter]*/",
                "mapping": ""
            }
        },
        "QueryConfiguration": {
            "RequiredFilters": [
                "startDate",
                "endDate"
            ]
        }
    }
]

