{
    "size": {
        "height": 100
    },
    "appointmentTemplate": "function getTemplate(data, index, element) {                
    if (element != null && element != undefined && element.style != null) {                       
        element.style.backgroundColor = "white";                         
        element.style.color = "grey";                
    }                
    if (data != null && data.appointmentData.items != null) {                         
        var appointmentHTML = "";                         
        data.appointmentData.items.forEach(function (val) { var noImageId = "noImage" + val.UserId + "_" + val.startDate;                                     
            appointmentHTML += "<div style='background-color: transparent; float:left;'><div style='width:25px;height:25px;margin:2px;'> <div id='" + noImageId + "' title='" + val.text + "' style='display:none;border-radius: 250px;overflow: hidden;width: 25px;height: 25px;border: 1px solid rgb(172, 172, 172);'> <div style='color: rgb(42, 128, 185); background-color: rgb(196, 219, 243); font-size: 12px; height: 100%; line-height: 25px;'>             <span style='text-align: center; font-weight: 300; display: block'>" + val.text.substr(0, 1).toUpperCase() + "</span> </div> </div> <img title='" + val.text + "' style='border-radius: 250px;width: 100%;height: 100%;display:block;' src='" + data.appointmentData.apiUrl +                         "users/" +                         val.UserId +                         "/imageStream'  onerror='this.style.display=\"none\";                                    if(document.getElementById(\"" + noImageId + "\") != null)                                         document.getElementById(\"" + noImageId + "\").style.display=\"block\"'></img></div></div>";                             
        }); 
        return appointmentHTML;                
    }         
}"
}

