DashboardDefinitionComponents.Parameters:
[
    {
        "WidgetSubTitle": "Tickets in the Selected Period",
        "WidgetType": "grid",
        "WidgetFilterField": "",
        "WidgetColor": "",
        "WidgetHeight": "",
        "WidgetHeader": {
            "showContextInfo": false,
            "WidgetPeriodChooser": {
                "type": "year",
                "visible": false
            },
            "WidgetMetrics": {
                "visible": true,
                "Metrics": [
                    {
                        "visible": true,
                        "metricName": "# Delayed To Do",
                        "metricFieldValue": "DelayedToDoJobsGrandTotal"
                    },
                    {
                        "visible": true,
                        "metricName": "# Delayed Work in Progress",
                        "metricFieldValue": "DelayedWorkInProgressJobsGrandTotal"
                    }
                ]
            },
            "showSubTitle": true,
            "showTitle": true
        },
        "WidgetDataSources": {
            "main": {
                "type": "queryText",
                "value": "select j.effort,j.NameAndExternalId as Job,ws.Name as Stage,j.Project,j.AgreedDateUtc as Enddate,j.Oid,isnull(ws.Color, wst.Color) as Color,datediff(day,getdate(),AgreedDateUtc) as Days,sum(iif(ws.name = 'To Do' and AgreedDateUtc < getdate(), 1, 0)) over() as DelayedToDoJobsGrandTotal,sum(iif(ws.name = 'Work in Progress' and AgreedDateUtc < getdate(), 1, 0)) over() as DelayedWorkInProgressJobsGrandTotal,(select min(ass.[User]) from Assignment ass, AssignmentType ast where ass.DocumentOid = j.Oid and ast.Name = 'Executor' and ass.AssignmentType = ast.Oid) as Executorfrom Deliverable j, WorkflowState ws, WorkflowStateType wst, Department depwhere ws.Oid = j.WorkflowState andwst.Oid = ws.WorkflowStateType anddep.Oid = j.Department anddep.Name in ('DevOps','Operations','Releases') andws.name in ('Work in Progress', 'Under Approval', 'To Do', 'Waiting', 'To Install')/*[DateFilter]*/ and j.AgreedDateUtc >= @FromDate and j.AgreedDateUtc <= @ToDate /*[/DateFilter]*/",
                "mapping": ""
            }
        },
        "QueryConfiguration": {
            "RequiredFilters": [
                "startDate",
                "endDate"
            ]
        }
    }
]

DashboardDefinitionComponents.Configuration:
{
  "height": "650",
  "onRowClick": "function(e){if(e.rowType=='data') SkillsWorkflow.preview(e.key, 'job');}",
  "keyExpr": "Oid",
  "selection": {
    "mode": "single"
  },
  "allowFiltering": true,
  "searchPanel": {
    "visible": true
  },
  "headerFilter": {
    "visible": true
  },
  "scrolling": {
    "mode": "virtual"
  },
  "columnMinWidth": 50,
  "grouping": {
    "autoExpandAll": true
  },
  "columns": [
    {
      "dataField": "Job",
      "name": "Jobs",
      "caption": "Jobs",
      "width": 670
    },
    {
      "dataField": "Days",
      "caption": "Days",
      "alignment": "right",
      "format": "#,##0",
      "width": 70,
      "cellTemplate": "function(e, i) {var h=e.append('<div>'+i.text+'</div>');
if(i.value<0) 
{h.children().css('text-align','center').css('min-width', '30px').css('background-color','#f5564a').css('color','#ffffff').css('border-radius','2px').css('padding','3px 7px').css('font-weight','700').css('white-space','nowrap').css('display','inline-block')
      };
if(i.value==0 || i.value==1)
{h.children().css('text-align','center').css('min-width', '30px').css('background-color','#ffc720').css('color','#ffffff').css('border-radius','2px').css('padding','3px 7px').css('font-weight','700').css('white-space','nowrap').css('display','inline-block')
      };
    }"
  },
  {
    "dataField": "effort",
    "caption": "Effort",
    "alignment": "right",
    "format": "#,##0",
    "width": 70
  },
  {
    "dataField": "Stage",
    "caption": "Stage",
    "name": "Name",
    "alignment": "left",
    "format": "#,##0",
    "width": 80,
    "groupCellTemplate": "function(e, i) {var color=i.component.option().dataSource.find(row=>row.Stage==i.text).Color;var h=document.createElement('span');h.style.backgroundColor = color; h.classList.add('documentStageColor');e.append(h);h=document.createElement('span'); var ht=' ' + i.text + ' ('+i.summaryItems[0].value+')'; h.innerHTML = ht;e.append(h);}",
    "groupIndex": 0
  },
  {
    "dataField": "Enddate",
    "caption": "End date",
    "alignment": "right",
    "dataType": "date",
    "format": "d MMM",
    "width": 100
  },
  {
    "dataField": "Executor",
    "caption": "Executor",
    "alignment": "right",
    "format": "#,##0",
    "width": 100,
    "cellTemplate": "function(e, i) {if(i.text == '') return; var h=e.append('<div style=\"text-align:center\"><img height=\"16\" width=\"16\" style=\"border-radius: 40px;\" src=\"'+SkillsWorkflow.getUserImages([i.text])+'\" /></div>');}"
  }
],
"sortByGroupSummaryInfo": [
  {
    "summaryItem": "count"
  }
],
"summary": {
  "groupItems": [
    {
      "column": "Jobs",
      "summaryType": "count",
      "valueFormat": "#,##0",
      "displayFormat": "{0}",
      "alignment": "left"
    }
  ],
  "totalItems": [
    {
      "column": "Jobs",
      "summaryType": "count",
      "valueFormat": "#,##0",
      "displayFormat": "Total: {0}",
      "alignment": "left"
    },
    {
      "column": "Effort",
      "summaryType": "sum",
      "valueFormat": "#,##0",
      "displayFormat": "{0}",
      "alignment": "center",
      "cellTemplate": "function(e, i) {var h=e.append('<div>'+i.text+'</div>');if(i.value<0) {h.css('background-color','#f5564a').css('color','#ffffff')}}"
    }
  ]
}
}

DashboardDefinitionComponents.Parameters:
[
    {
        "WidgetSubTitle": "Closed and In Progress Tickets",
        "WidgetType": "rangeSelector",
        "WidgetFilterField": "",
        "WidgetColor": "#e0e0e0",
        "WidgetHeight": "",
        "WidgetHeader": {
            "showContextInfo": false,
            "WidgetPeriodChooser": {
                "startDate": "2019/05/1",
                "endDate": "2019/09/30",
                "type": "year",
                "visible": false
            },
            "showSubTitle": true,
            "showTitle": true
        },
        "WidgetDataSources": {
            "main": {
                "type": "queryText",
                "value": "select max(datepart(ww, j.AgreedDateUtc)) as week,min(j.AgreedDateUtc) as WeekStart,sum(iif(ws.name = 'Done', 1, 0)) as Done,sum(iif(ws.name != 'Done', 1, 0)) as ToDo from Deliverable j,WorkflowState ws,WorkflowStateType wst,Department dep where   ws.Oid = j.WorkflowState and wst.Oid = ws.WorkflowStateType and dep.Oid = j.Department and dep.Name in ('DevOps','Operations') and ws.name in ('Done', 'Work in Progress', 'Under Approval', 'To Do', 'Waiting') and j.AgreedDateUtc >= @FromDate and j.AgreedDateUtc <= @ToDate group by datepart(yyyy, j.AgreedDateUtc), datepart(ww, j.AgreedDateUtc)order by WeekStart",
                "mapping": ""
            }
        },
        "QueryConfiguration": null
    }
]

