[
    {
        "WidgetSubTitle": "Bars represent weeks with Overtime",
        "WidgetType": "rangeSelector",
        "WidgetFilterField": "",
        "WidgetColor": "#ffffff",
        "WidgetHeight": "135",
        "WidgetHeader": {
            "showContextInfo": false,
            "WidgetPeriodChooser": {
                "startDate": "2018/06/1",
                "endDate": "2019/06/30",
                "type": "year",
                "visible": false
            },
            "showSubTitle": true,
            "showTitle": true
        },
        "WidgetDataSources": {
            "main": {
                "type": "queryText",
                "value": "SELECT v.[week], v.WeekStart, sum(v.Overtime) as Overtime FROM ( SELECT MAX(DATEPART(ww, t.[DATE])) AS [Week] ,MAX(t.[DATE]) AS WeekStart ,SUM(IIF((t.ActualTime) > 8, 1, 0)) AS Overtime FROM [User] u ,Analytics.DatamartTimesheet t WHERE u.PaidOvertime = '1' AND t.UserId = u.Oid GROUP BY DATEPART(yyyy, t.[DATE]) ,DATEPART(ww, t.[DATE]) UNION ALL SELECT MAX(DATEPART(ww, yd.[Day])) AS week, MAX(yd.[Day]) AS WeekStart, 0 as Overtime FROM YearDay yd WHERE yd.[Day] between DateAdd(mm, -6, GETDATE()) AND DateAdd(mm, 1, GETDATE()) GROUP BY DATEPART(yyyy, yd.[Day]), DATEPART(ww, yd.[Day]) ) v GROUP BY v.[week], v.WeekStart ORDER BY v.WeekStart",
                "mapping": ""
            }
        },
        "QueryConfiguration": null
    }
]

