[
    {
        "WidgetSubTitle": "Time keyed-in in current period but belonging to another period",
        "WidgetType": "grid",
        "WidgetFilterField": "",
        "WidgetColor": "#ffffff",
        "WidgetHeight": "135",
        "WidgetHeader": {
            "showContextInfo": false,
            "WidgetPeriodChooser": {
                "type": "year",
                "visible": false
            },
            "WidgetMetrics": {
                "visible": false,
                "Metrics": [
                    {
                        "visible": true,
                        "metricName": "? Un-approved Time",
                        "metricFieldValue": "UnapprovedTime"
                    }
                ]
            },
            "showSubTitle": true,
            "showTitle": true
        },
        "WidgetDataSources": {
            "main": {
                "type": "queryText",
                "value": "SELECT u.[Name] AS [User] ,CAST(t.[Date] AS DATE) AS [Date] ,SUM(t.[ActualTime]) AS Timesheet FROM [User] u ,Analytics.DatamartTimesheet t ,Timesheet ot WHERE u.PaidOvertime = '1' AND t.UserId = u.Oid AND t.Oid = ot.Oid AND ot.CreatedOn >= @FromDate AND ot.CreatedOn < @ToDate AND NOT t.[Date] >= @FromDate AND t.[Date] < @ToDate GROUP BY u.Name ,CAST(t.[Date] AS DATE)",
                "mapping": ""
            }
        },
        "QueryConfiguration": {
            "RequiredFilters": [
                "startDate",
                "endDate"
            ]
        }
    }
]

