DashboardDefinitionComponents.Parameters:
[
    {
        "WidgetSubTitle": "Tickets from Client Service and Support by Stage",
        "WidgetType": "chart",
        "WidgetFilterField": "",
        "WidgetColor": "",
        "WidgetHeight": "350",
        "WidgetHeader": {
            "showContextInfo": false,
            "WidgetPeriodChooser": {
                "type": "year",
                "visible": false
            },
            "WidgetMetrics": {
                "visible": true,
                "Metrics": [
                    {
                        "visible": true,
                        "metricName": "? Open",
                        "metricFieldValue": "OpenTotal"
                    },
                    {
                        "visible": true,
                        "metricName": "? Delayed",
                        "metricFieldValue": "DelayedTotal"
                    },
                    {
                        "visible": true,
                        "metricName": "? Last Activity",
                        "metricFieldValue": "LastActivityTotall"
                    },
                    {
                        "visible": true,
                        "metricName": "? RequestApproval",
                        "metricFieldValue": "RequestApprovalTotal"
                    },
                    {
                        "visible": true,
                        "metricName": "? RequestMoreInfo",
                        "metricFieldValue": "RequestMoreInfoTotal"
                    }
                ]
            },
            "showSubTitle": true,
            "showTitle": true
        },
        "WidgetDataSources": {
            "main": {
                "type": "queryText",
                "value": "select 'Open' as [Open], 'Delayed' as Delayed, 'Request Approval' as RequestApproval, 'Request More Information' as RequestMoreInformation, 'Last Activity' as LastActivity, 0 as OpenTotal, sum(iif(ws.name in ('in Tests','Move to Production','on Hold', 'Request Uat', 'Requirement Approval', 'Under Analysis', 'To Install') and AgreedDateUtc < getdate(), 1, 0)) as DelayedTotal, sum(iif(ws.name in ('Request Approval'), 1, 0)) as RequestApprovalTotal, sum(iif(ws.name in ('Request more information'), 1, 0)) as RequestMoreInfoTotal, sum(iif((getdate() - j.ModifiedOn)>7,1,0)) as LastActivityTotal from Deliverable j, WorkflowState ws, WorkflowStateType wst, Department dep where ws.Oid = j.WorkflowState and wst.Oid = ws.WorkflowStateType and dep.Oid = j.Department and dep.Name in ('Support','Client Service') and ws.name in ('Open', 'in Tests','Move to Production','on Hold', 'Request Uat', 'Requirement Approval', 'Under Analysis', 'To Install', 'Request Approval', 'Request more information') /*[DateFilter]*/ and (j.AgreedDateUtc >= @FromDate and j.AgreedDateUtc <= @ToDate and ws.name not in ('Open'))/*[/DateFilter]*/ union all select 'Open' as [Open], 'Delayed' as Delayed, 'Request Approval' as RequestApproval, 'Request More Information' as RequestMoreInformation, 'Last Activity' as LastActivity, sum(iif(ws.name in ('Open'), 1, 0)) as OpenTotal, 0 as DelayedTotal, 0 as RequestApprovalTotal, 0 as RequestMoreInfoTotal, 0 as LastActivityTotal from Deliverable j, WorkflowState ws, WorkflowStateType wst, Department dep where ws.Oid = j.WorkflowState and wst.Oid = ws.WorkflowStateType and dep.Oid = j.Department and dep.Name in ('Support','Client Service') and ws.name in ('Open', 'in Tests','Move to Production','on Hold', 'Request Uat', 'Requirement Approval', 'Under Analysis', 'To Install', 'Request Approval', 'Request more information') /*[DateFilter]*/ and (j.EntryDateUtc >= @FromDate and j.EntryDateUtc <= @ToDate) /*[/DateFilter]*/"
            }
        },
        "QueryConfiguration": {
            "RequiredFilters": [
                "startDate",
                "endDate"
            ]
        }
    }
]

[
    {
        "WidgetSubTitle": "Tickets in Selected Period",
        "WidgetType": "grid",
        "WidgetFilterField": "",
        "WidgetColor": "",
        "WidgetHeight": "",
        "WidgetHeader": {
            "showContextInfo": false,
            "WidgetPeriodChooser": {
                "type": "year",
                "visible": false
            },
            "WidgetMetrics": {
                "visible": true,
                "Metrics": [
                    {
                        "visible": true,
                        "metricName": "? Delayed Critical",
                        "metricFieldValue": "DelayedCritical"
                    }
                ]
            },
            "showSubTitle": true,
            "showTitle": true
        },
        "WidgetDataSources": {
            "main": {
                "type": "queryText",
                "value": "select jcl.Name as JobClassification, j.NameAndExternalId as Job, ws.Name as Stage, j.Project, iif(ws.name in ('Open'), j.EntryDateUtc, j.AgreedDateUtc) as Enddate, j.Oid, ISNULL(ws.Color, wst.Color) as Color, DATEDIFF(day,getdate(),AgreedDateUtc) as Days, DATEDIFF(day,ModifiedOn,getdate()) as LastActivity, sum(iif((jcl.Name in ('Urgent', 'Necessary', 'Important') and AgreedDateUtc < getdate()), 1 ,0)) over() as DelayedCritical from Deliverable j, WorkflowState ws, WorkflowStateType wst, Department dep, JobClassification jcl where ws.Oid = j.WorkflowState and wst.Oid = ws.WorkflowStateType and dep.Oid = j.Department and j.Classification = jcl.Oid and dep.Name in ('Support','Client Service') and ((ws.name in ('in Tests','Move to Production','on Hold', 'Request Uat', 'Requirement Approval', 'Under Analysis', 'To Install','Request Approval','Request more information') /*[DateFilter]*/ and j.AgreedDateUtc >= @FromDate and j.AgreedDateUtc <= @ToDate /*[/DateFilter]*/) or (ws.name in ('Open') /*[DateFilter]*/ and j.EntryDateUtc >= @FromDate and j.EntryDateUtc <= @ToDate /*[/DateFilter]*/))",
                "mapping": ""
            }
        },
        "QueryConfiguration": {
            "RequiredFilters": [
                "startDate",
                "endDate"
            ]
        }
    }
]

