DashboardDefinitionComponents.Parameters:
[
    {
        "WidgetSubTitle": "Closed, Critical and In Progress Tickets",
        "WidgetType": "rangeSelector",
        "WidgetFilterField": "",
        "WidgetColor": "#e0e0e0",
        "WidgetHeight": "100",
        "WidgetHeader": {
            "showContextInfo": false,
            "WidgetPeriodChooser": {
                "startDate": "2019/01/1",
                "endDate": "2019/12/31",
                "type": "year",
                "visible": false
            },
            "showSubTitle": true,
            "showTitle": true
        },
        "WidgetDataSources": {
            "main": {
                "type": "queryText",
                "value": "select max(week) as week, [Day], iif(sum([Open]) = 0, null, max(sum(InProgress)) over() + sum([Open])) as [Open], sum(InProgress) as InProgress, iif(sum(Critical) = 0, null, max(sum(Closed)) over() + sum(Critical)) as Critical, sum(Closed) as Closed from ( select max(datepart(ww, j.EntryDateUtc)) as week, min(cast(j.EntryDateUtc as date)) as [Day], sum(iif(ws.name = 'Open', 1, 0)) as [Open], 0 as InProgress, 0 as Critical, 0 as Closed from Deliverable j, WorkflowState ws, WorkflowStateType wst, Department dep, JobClassification jcl where ws.Oid = j.WorkflowState and wst.Oid = ws.WorkflowStateType and dep.Oid = j.Department and j.Classification = jcl.Oid and dep.Name in ('Support', 'Client Service') and ws.name in ('Canceled', 'Closed(Unrelated)', 'Closed', 'Open', 'in Tests', 'Move to Production', 'on Hold', 'Request Uat', 'Requirement Approval', 'Under Analysis', 'To Install', 'Request Approval', 'Request more information') and cast(j.EntryDateUtc as date) >= @FromDate and cast(j.EntryDateUtc as date) <= @ToDate group by datepart(yyyy, j.EntryDateUtc), datepart(ww, j.EntryDateUtc) union all select max(datepart(ww, j.AgreedDateUtc)) as week, min(cast(j.AgreedDateUtc as date)) as [Day], 0 as [Open], sum(iif(ws.name in ('in Tests','Move to Production','on Hold', 'Request Uat', 'Requirement Approval', 'Under Analysis', 'To Install', 'Request Approval', 'Request more information'), 1, 0)) as InProgress, sum(iif(jcl.Name in ('Urgent', 'Necessary', 'Important') and ws.name not in ('Closed', 'Canceled', 'Closed(Unrelated'), 1, 0)) as Critical, sum(iif(ws.name in ('Canceled', 'Closed(Unrelated)', 'Closed'), 1, 0)) as Closed from Deliverable j, WorkflowState ws, WorkflowStateType wst, Department dep, JobClassification jcl where ws.Oid = j.WorkflowState and wst.Oid = ws.WorkflowStateType and dep.Oid = j.Department and j.Classification = jcl.Oid and dep.Name in ('Support', 'Client Service') and ws.name in ('Canceled', 'Closed(Unrelated)', 'Closed', 'Open', 'in Tests', 'Move to Production', 'on Hold', 'Request Uat', 'Requirement Approval', 'Under Analysis', 'To Install', 'Request Approval', 'Request more information') and cast(j.AgreedDateUtc as date) >= @FromDate and cast(j.AgreedDateUtc as date) <= @ToDate group by datepart(yyyy, j.AgreedDateUtc), datepart(ww, j.AgreedDateUtc) ) t group by [Day] order by [Day]"
            }
        },
        "QueryConfiguration": null
    }
]
