            "main": {
                "type": "querytext",
                "value": "SELECT DATEADD(dd, -(DATEPART(dw, dt.[Date])-2), dt.[Date]) AS WeekStart, \r\n\t\t[Year], [Month], \r\n\t\tConvert(int, CONCAT(year(dt.[Date]), DatePart(wk, dt.[Date]))) as [YearWeek], \r\n\t\tSUM(dt.[ActualTime]) as [Hours]\r\nFROM [Analytics].Timesheet(@CurrentUserId) dt \r\nWHERE Year(dt.[Date]) = @year and\r\n\t\t\t\t\t\t(dt.[CompanyId]  = @Company or @Company = '00000000-0000-0000-0000-000000000000') and\r\n\t\t\t\t\t\t(dt.[DivisionId] = @Division or @Division = '00000000-0000-0000-0000-000000000000') and\r\n\t\t\t\t\t\t(dt.[DepartmentId] = @Department or @Department = '00000000-0000-0000-0000-000000000000')\r\n\t\t\t\t\t\tand (dt.[ClientId] = @Client or @Client = '00000000-0000-0000-0000-000000000000')\r\n                        and (dt.[ProjectId] = @Project or @Project = '00000000-0000-0000-0000-000000000000')\r\n                        and (dt.[JobId] = @Job or @Job = '00000000-0000-0000-0000-000000000000')\r\nGROUP BY DATEADD(dd, -(DATEPART(dw, dt.[Date])-2), dt.[Date]), [Year], [Month], Convert(int, CONCAT(year(dt.[Date]), DatePart(wk, dt.[Date])))",
                "mapping": ""
            }
        },
        "Filters": {},
        "QueryConfiguration": {
            "PreviousPeriod": {
                "Show": false,
                "KeyFieldName": "",
                "Type": 0
