[
    {
        "WidgetSubTitle": "Pendente Neg?cio, Em Execu??o, Em Distribui??o e Fechadas",
        "WidgetType": "chart",
        "WidgetFilterField": "",
        "WidgetColor": "#e0e0e0",
        "WidgetHeight": 200,
        "WidgetHeader": {
            "showContextInfo": false,
            "WidgetPeriodChooser": {
                "type": "year",
                "visible": false
            } ,
            "WidgetMetrics": {
                "visible": true,
                "Metrics": [
                    {
                        "visible": true,
                        "metricName": "# Em Distribui??o",
                        "metricFieldValue": "TotalBeingPrepared"
                    } ,
                    {
                        "visible": true,
                        "metricName": "# Em Execu??o",
                        "metricFieldValue": "TotalInProgress"
                    } ,
                    {
                        "visible": true,
                        "metricName": "# Pendente Neg?cios",
                        "metricFieldValue": "TotalUnderApproval"
                    } ,
                    {
                        "visible": true,
                        "metricName": "# Finalizado",
                        "metricFieldValue": "TotalDone"
                    } 
                ]
            } ,
            "showSubTitle": true,
            "showTitle": true
        } ,
        "WidgetDataSources": {
            "main": {
                "type": "querytext",
                "value": "select\tv.*,\r\n\t\t(sum(v.BeingPrepared) over()) as TotalBeingPrepared,\r\n\t\t(sum(v.UnderApproval) over()) as TotalUnderApproval,\r\n\t\t(sum(v.InProgress) over()) as TotalInProgress,\r\n\t\t(sum(v.Done) over()) as TotalDone\r\nfrom\t(\r\n\t\tselect\tdj.Department,\r\n\t\t\t\tsum(iif(dj.StateTypeCode in (3,15), 1, 0)) as BeingPrepared,\r\n\t\t\t\tsum(iif(dj.StateTypeCode = 5, 1, 0)) as UnderApproval,\r\n\t\t\t\tsum(iif(dj.StateTypeCode = 4, 1, 0)) as InProgress,\r\n\t\t\t\tsum(iif(dj.StateTypeCode = 6, 1, 0)) as Done\r\n\t\tfrom\tAnalytics.Job(@CurrentUserId) dj\r\n\t\twhere\tdj.[Date] between @FromDate and @ToDate and\r\n\t\t\t\t(dj.DivisionId = @Division or @Division = '00000000-0000-0000-0000-000000000000')\r\n\t\tgroup by dj.Department\r\n) v\r\nwhere\tv.BeingPrepared != 0 or\r\n\t\tv.UnderApproval != 0 or\r\n\t\tv.InProgress != 0 or \r\n\t\tv.Done != 0\r\ngroup by v.Department,\r\n\t\tv.BeingPrepared,\r\n\t\tv.UnderApproval,\r\n\t\tv.InProgress,\r\n\t\tv.Done",
                "mapping": ""
            } 
        } ,
        "Filters": {} ,
        "QueryConfiguration": {
            "RequiredFilters": [
                "startDate",
                "endDate",
                "Division"
            ]
        } 
    } 
]

