[
    {
        "WidgetSubTitle": "Finalizado (azul) e Em Progresso (vermelho)",
        "WidgetType": "rangeselector",
        "WidgetFilterField": "",
        "WidgetColor": "#e0e0e0",
        "WidgetHeight": 200,
        "WidgetHeader": {
            "showContextInfo": false,
            "WidgetPeriodChooser": {
                "type": "year",
                "visible": false
            } ,
            "showSubTitle": true,
            "showTitle": true
        } ,
