            "main": {
                "type": "querytext",
                "value": "select\tv.[Week],\n\t\tsum(v.InProgress) as InProgress,\n\t\tsum(v.Done) as Done\nfrom\t(\n\tselect\tmax(dj.[Date] - datepart(dw, dj.[Date]) + 1) as Week,\n\t\t\tsum(iif(dj.StateTypeCode = 4, 1, 0)) as InProgress,\n\t\t\tsum(iif(dj.StateTypeCode = 6, 1, 0)) as Done\n\tfrom\tAnalytics.Job(@CurrentUserId) dj\n\twhere\tYear(dj.[Date]) = @year and\n\t\t\t(dj.DivisionId = @Division or @Division = '00000000-0000-0000-0000-000000000000')\n\tgroup by dj.[Date]\n) v\nwhere\tv.InProgress != 0 or v.Done != 0\ngroup by v.[Week]",
                "mapping": ""
            } 
        } ,
        "Filters": {} ,
        "QueryConfiguration": {
            "RequiredFilters": [
                "year",
                "Division"
            ]
        } 
    } 
]

