{
    "onDrawn": "function(s) {
      var sunday = new Date();
      var lastDayOfWeek = 7 - sunday.getDay();
      sunday.setDate(sunday.getDate() + lastDayOfWeek); 
      var monday = new Date(sunday);
      monday.setDate(monday.getDate() - 6);
      monday.setHours(0, 0, 0, 0);
      sunday.setHours(23, 59, 59, 59);
      s.component.option('value', [monday, sunday]);
    }",
