{
    "colCount": "2",
    "items": [
        {
            "name": "Start Date",
            "label": {
                "text": "Start Date"
            },
            "editorType": "dxDateBox",
            "editorOptions": {
                "width": 200,
                "onFocusIn": "function(e) {                    var endDate = SW.getComponent('endDate');                    e.component.option('max', endDate);                }",
                "onInitialized": "function(e) {
                    var date = new Date();
                    var startDate = date.getFullYear() + '-' + 1 + '-' + 1;
                    SW.saveComponent(startDate, 'startDate');
                    var endDate = date.getFullYear() + '-' + 12 + '-' + 31;
                    e.component.option('max', endDate);
                    var showDate = new Date(startDate);
                    e.component.dateValue(showDate);
                    SW.setDashboardFilter('FromDate', startDate);
                    setTimeout(function() {
                            SW.setDashboardFilter('ToDate', endDate);
                    },500);
                }",
                "onValueChanged": "function(e) {                    var startDate = e.value.getFullYear() + '-' + (e.value.getMonth() + 1) + '-' + e.value.getDate();                    SW.saveComponent(startDate, 'startDate');                    SW.setDashboardFilter('FromDate', startDate);                }"
            }
        },
        {
            "name": "End Date",
            "label": {
                "text": "End Date"
            },
            "editorType": "dxDateBox",
            "editorOptions": {
                "width": 200,
                "onFocusIn": "function(e) {                    var startDate = SW.getComponent('startDate');                    e.component.option('min', startDate);                }",
                "onInitialized": "function(e) {
                    var date = new Date();
                    var endDate = date.getFullYear() + '-' + 12 + '-' + 31;SW.saveComponent(endDate, 'endDate');                    var startDate = date.getFullYear() + '-' + 1 + '-' + 1;                    e.component.option('min', startDate);                    var showDate = new Date(endDate);                    e.component.dateValue(showDate);                }",
                "onValueChanged": "function(e) {                    var endDate = e.value.getFullYear() + '-' + (e.value.getMonth() + 1) + '-' + e.value.getDate();                    SW.saveComponent(endDate, 'endDate');                    SW.setDashboardFilter('ToDate', endDate);                }"
            }
        }
    ],
    "height": 72
}

