    "onRowUpdating": "function(e) {
    if (e.newData.effort) {
        var body = '{ "Effort": { "Value":' + e.newData.effort + '} }';
        SW.executeAPI('PATCH', 'jobs/' + e.key, null, body);
        var urlEffortUpdate = 'https://dev.azure.com/[{organization}/{project}]/_apis/wit/workitems/' + e.oldData.WorkItem + '?api-version=5.0';
        var updateEffortBody = '[{"op":"add","path":"/fields/Microsoft.VSTS.Scheduling.Effort","from":null,"value":"' + e.newData.effort + '"}]';
        fetch(urlEffortUpdate, {
            method: "PATCH",
            headers: {
                "Authorization": "Basic Om1zeGJrczdpaGtzNnlkeGplcW9vYWtvdW5ob21pNHlwbW16NWpscWplcDNxNnVkM3BmZHE=",
                "Content-Type": "application/json-patch+json"
            },
            body: updateEffortBody
        });
    }
    if (e.newData.IterationDate) {
        var e = e;

        SW.executeAPI('PUT', 'v3/documentUserFieldValues', null, {
            'DocumentTypeName': 'Deliverable',
            'DocumentId': e.key,
            'DocumentUserFieldValues': Object.entries(e.newData).map(([k, v]) => ({
                'ColumnName': 'Iteration Date',
                'ColumnDataTypeId': 1,
                'Value': v
            }))
        });
        async function updateIteration(e) {
            var url = 'https://dev.azure.com/[{organization}/{project}/{team}]/_apis/work/teamsettings/iterations';
            var test = await fetch(url, {
                method: "GET",
                headers: {
                    "Authorization": "Basic Om1zeGJrczdpaGtzNnlkeGplcW9vYWtvdW5ob21pNHlwbW16NWpscWplcDNxNnVkM3BmZHE="
                }
            });
            var iterations = await test.json();
            var iteration = iterations.value.find(i => (e.newData.IterationDate.split('T')[0] >= i.attributes.startDate.split('T')[0]) && (e.newData.IterationDate.split('T')[0] <= i.attributes.finishDate.split('T')[0]));
            var urlUpdate = 'https://dev.azure.com/[{organization}/{project}]/_apis/wit/workitems/' + e.oldData.WorkItem + '?api-version=5.0';
            var iterationToUpdate = 'Skill\\\\' + iteration.name;
            var updateBody = '[            {"op":"add","path":"/fields/System.IterationPath","from":null,"value":"' + iterationToUpdate + '"}]';
            fetch(urlUpdate, {
                method: "PATCH",
                headers: {
                    "Authorization": "Basic Om1zeGJrczdpaGtzNnlkeGplcW9vYWtvdW5ob21pNHlwbW16NWpscWplcDNxNnVkM3BmZHE=",
                    "Content-Type": "application/json-patch+json"
                },
                body: updateBody
            });
            updateIteration;
        }
        updateIteration(e)
    }
}",
