{
    "dataSource": {
        "load": "function(e){

                   var paths = [
        'Standard/Cloud/Configuration/en/',
        'Standard/Cloud/Document/Transition/en/',
        'Standard/Cloud/Document/Type/en/',
        'Standard/Cloud/Document/User/en/',
        'Standard/Cloud/Document/Workflow/en/',
        'Standard/Cloud/Document/WorkflowState/en/',
        'Standard/Cloud/Popup/Integrations/en/',
        'Standard/Cloud/Popup/LeaveTypes/en/',
        'Standard/Cloud/Popup/Templates/en/',
        'Standard/Cloud/Popup/User/en/',
        'Non-standard/Cloud/Menu/en/'
                ];

                    var auth = 'token {githubToken}';
					return SkillsWorkflow.Github.getAllDashboardsFrom(auth, paths).then(
                        result => {
                            const top = 10;
                            result = result.sort((a, b) => b.Changes-a.Changes);
                            result = result.slice(0, top);
                            return Promise.resolve(result)
        }
                    )
    }"
