            "showTitle": true
        },
        "WidgetDataSources": {
            "main": {
                "type": "queryText",
                "value": "select v.*, (sum(v.DelayedJobs) over ()) as DelayedJobsGrandTotal, (sum(v.WaitingJobs) over ()) as WaitingJobsGrandTotal, (sum(v.OnHoldJobs) over ())as OnHoldJobsGrandTotal, (sum(v.OnGoingJobs) over ()) as OnGoingJobsGrandTotal, (sum(v.DelayedProjects) over ()) as DelayedProjectsGrandTotal, (sum(v.WaitingProjects) over ()) as WaitingProjectsGrandTotal, (sum(v.OnHoldProjects) over ())as OnHoldProjectsGrandTotal, (sum(v.OnGoingProjects) over ()) as OnGoingProjectsGrandTotal from ( select v.Client, sum(v.DelayedJobs) as DelayedJobs, sum(v.WaitingJobs) as WaitingJobs, sum(v.OnHoldJobs) as OnHoldJobs, sum(v.OnGoingJobs) as OnGoingJobs, sum(v.DelayedProjects) as DelayedProjects, sum(v.WaitingProjects) as WaitingProjects, sum(v.OnHoldProjects) as OnHoldProjects, sum(v.OnGoingProjects) as OnGoingProjects from ( select v.Client, v.Project, sum(iif(v.IsDelayed = 1, 1, 0)) as DelayedJobs, sum(iif(v.IsWaiting = 1, 1, 0)) as WaitingJobs, sum(iif(v.IsOnHold = 1, 1, 0)) as OnHoldJobs, sum(iif(v.IsOnGoing = 1, 1, 0)) as OnGoingJobs, max(iif(v.ProjectIsDelayed = 1, 1, 0)) as DelayedProjects, max(iif(v.ProjectIsWaiting = 1, 1, 0)) as WaitingProjects, max(iif(v.ProjectIsOnHold = 1, 1, 0)) as OnHoldProjects, max(iif(v.ProjectIsOnGoing = 1, 1, 0)) as OnGoingProjects from SkillAnalyticsExecutionStatus v, Project p, UserUsers_CompanyCompanies uucc where (1 = 1) /*[DateFilter]*/ and cast(v.[Date] as Date) between @FromDate and @ToDate /*[/DateFilter]*/ and p.Oid = v.ProjectId and uucc.Users = @CurrentUserId and uucc.Companies = p.Company and Exists(select ccc.CommercialClient from CommercialClientCompany ccc, UserUsers_CompanyCompanies uucc where ccc.Company = uucc.Companies and uucc.Users = @CurrentUserId and ccc.CommercialClient = p.Client) group by v.Client, v.Project ) v group by v.Client ) v",
                "mapping": ""
            }
        },
        "QueryConfiguration": {}
    }
]

