[
    {
        "WidgetSubTitle": "Re-Work per Executors",
        "WidgetType": "pie",
        "WidgetFilterField": "",
        "WidgetColor": "",
        "WidgetHeight": "",
        "WidgetHeader": {
            "showContextInfo": false,
            "WidgetPeriodChooser": {
                "type": "year",
                "visible": false
            },
            "WidgetMetrics": {
                "visible": true,
                "Metrics": [
                    {
                        "visible": true,
                        "metricName": "# Projects",
                        "metricFieldValue": "JobsGrandTotal"
                    }
                ]
            },
            "showSubTitle": true,
            "showTitle": true
        },
        "WidgetDataSources": {
            "main": {
                "type": "queryText",
                "value": "With Executors As ( SELECT v.JobId, STRING_AGG(v.ChangedBy, ', ') as Names FROM ( select h.DocumentOid as JobId, usr.UserName as ChangedBy, Min(h.StartDate) as ModifiedOn from History h, WorkflowState hws, WorkflowStateType hwst, SecuritySystemUser usr, [User] u, UserUsers_CompanyCompanies uucc where h.DocumentTypeName = 'Skill.Module.BusinessObjects.Deliverable' and hwst.Oid = hws.WorkflowStateType and h.WorkflowState = hws.Oid and usr.Oid = h.[User] and hwst.[Status] = 5 and u.Oid = usr.Oid and uucc.Users = @CurrentUserId and uucc.Companies = u.Company group by h.DocumentOid, usr.UserName ) v GROUP BY v.JobId ), ReWorkJobs As ( select h.DocumentOid as JobId, h.StartDate as ModifiedOn from History h, WorkflowState hws, WorkflowStateType hwst, SecuritySystemUser usr where h.DocumentTypeName = 'Skill.Module.BusinessObjects.Deliverable' and hwst.Oid = hws.WorkflowStateType and h.WorkflowState = hws.Oid and usr.Oid = h.[User] and hwst.[Status] in (13, 18)) select v.*, (sum(v.Jobs) over ()) JobsGrandTotal from ( SELECT v.Executors, sum(1) as Jobs FROM ( SELECT rj.JobId, ex.Names as Executors FROM Executors ex left outer join ReWorkJobs rj on (ex.JobId = rj.JobId) WHERE ex.Names is not null ) v group by v.Executors ) v",
                "mapping": ""
            }
        },
        "QueryConfiguration": null
    }
]

