DashboardDefinitionComponents.Configuration:
{
  "allowBulk": false,
  "dataSource": {
    "load": "function(){           
     return SkillsWorkflow.executeAPI('GET', 'integration-workflows', null, null).then(result => {
         return Promise.resolve(result)        
        });    
}"
  },
  "columns": [
    {
      "visibleIndex": 0,
      "type": "buttons",
      "buttons": [
        {
          "hint": "Start automation workflow",
          "visible": true,
          "icon": "fal fa-play",
          "onClick": "function(e){                         e.event.preventDefault();                          SW.Toastr.toastr_Info('Automation workflow "'+e.row.data.name+'" started.');                                        var runAutomation =  SkillsWorkflow.executeAPI('GET','integration-workflows/'+e.row.data.id+'/execute').then(r => {SW.Toastr.toastr_Info('Automation workflow "'+e.row.data.name+'" finished.');     })}"
        },
        {
          "hint": "View log",
          "visible": true,
          "icon": "fal fa-history",
          "onClick": "function(e){ e.event.preventDefault();                  var data = e.row.data; var workSpace = '2d86da83-cfa1-43c7-89b3-3c6d0c8b67fb'; SW.genericPopup('automation', [{ Id: workSpace, Title: 'Log'}], data, '90%', '90%', false);    }"
        },
        {
          "hint": "Get automation workflow url",
          "visible": true,
          "icon": "fal fa-link",
          "onClick": "function(e){                         e.event.preventDefault();                                                          var aux = document.createElement('textarea');                                document.body.appendChild(aux);                                aux.value = 'https://apiv2-'+window.location.host+'/api/integration-workflows/'+e.row.data.id+'/execute';                                aux.select();                                document.execCommand('copy');                                document.body.removeChild(aux);                                 SW.Toastr.toastr_Success('Automation workflow "'+e.row.data.name+'" URL copied successfully.');                                                                 }"
        }
      ]
    },
    {
      "dataField": "name",
      "caption": "Name"
    },
    {
      "dataField": "active",
      "caption": "Active"
    }
  ],
  "onRowClick": "function(e) {
    if (e.rowType == 'data')
    var id = 'ffdb36dc-a0c8-4c3c-a87b-4b64c325db0f';
    SW.executeAPI("GET", "integration-workflows/" + e.data.id).then(result => {SW.genericPopup('automation', [{ Id: id, Title: e.data.name }], result, '80%', '80%', true) })
}",
  "onToolbarPreparing": "function(e) {
    var dataGrid = e.component;
    e.toolbarOptions.items.unshift({
        location: 'after',
        template: 'toggleHeaderFilterButtonTemplate'
    });
    e.toolbarOptions.items.unshift({
        location: 'after',
        widget: 'dxButton',
        options: {
            icon: 'fal fa-times',
            onClick: function (e) {
                SW.genericDelete('automation', dataGrid, 'id');
            }
        }
    });
    e.toolbarOptions.items.unshift({
        location: 'after',
        widget: 'dxButton',
        options: {
            icon: 'fal fa-plus',
            onClick: function (e) {
                SW.genericNew('automation');
            }
        }
    });
    e.toolbarOptions.items.unshift({
        location: 'after',
        widget: 'dxButton',
        options: {
            icon: 'fal fa-store',
            hint: 'Store',
            onClick: function (e) {
                SW.genericPopup('workspace', [{ Id: '766627fc-873f-410c-b87b-5408758b3044', Title: 'Automation Store' }], '', '90%', '90%', true);
            }
        }
    });
}",
  "height": "100%",
  "entity": "automation"
}
