{"dataSource":{"load":"function f(e) {
    var url = "";
    var config = SW.getConfiguration();
    if (config.environmentType == 0) {
        url = "https://skillsworkspacesstore.blob.core.windows.net/development/dashboardslist.json"
    } else if (config.environmentType == 1) {
        url = "https://skillsworkspacesstore.blob.core.windows.net/test/dashboardslist.json"
    } else if (config.environmentType == 2) {
        url = "https://skillsworkspacesstore.blob.core.windows.net/production/dashboardslist.json"
    }
    return SkillsWorkflow.Storage.getWorkspacesFromStorage(url).then(
        x => {
            var bla = x.map(x => {
                    return {Name: x.Name, Changes: x.LastCommit.Changes}
                }
            );
            return bla.sort((a, b) => {
                return b.Changes - a.Changes}).slice(0, 10);
        }
    );
