    var companyId = object["CompanyId"];
    debugger;
    var externalTableModel = {
        KeyValue: companyId,
        KeyColumnName: integrator.KeyColumnName,
        Columns: GetColumns(object, integrator)
    };
    debugger;
    SW.executeAPI(
        'PUT',
         'externalTables/'+integrator.TableName+'/rows',
         null,
         JSON.stringify(externalTableModel)
    ).then(
        x => {
            form.option('disabled', false);
            e.component.option('disabled', false);
            SW.Toastr.toastr_Success('Saved');
        }
    ).catch(
        x => {
            form.option('disabled', false);
            e.component.option('disabled', false);
            SW.Toastr.toastr_Error(x);
        }
    )

    function GetColumns(object, integrator){
        var columns = [];
        var config = JSON.parse(integrator.ConfigurationMetadata);
        config.columns.forEach(column => {
            var column = {
                ColumnName: column.dataField,
                Value: GetValue(object, column.dataField)
            }
            columns.push(column);
        });
        return columns;
    }

    function GetValue(p, key){
        if(p[key] != null){
            return p[key];
        }
        return "";
    }
