{"colCount":"1","labelLocation":"left","scrollingEnabled":true,"width":"100%","height":"100%","onInitialized":"function(e){
    var button = SW.getComponent('saveButton');
    var integratorsGrid = SW.getComponent('data');
    var integrator = integratorsGrid.getSelectedRowsData()[0];

    SW.saveComponent(e.component, 'formIntegrator');
    debugger;
    SW.executeAPI('GET', 'integrators/'+integrator.Id)
        .then(x => {
            if(x.ConfigurationMetadata){
                var config = JSON.parse(x.ConfigurationMetadata);
                SetCompanyColumn(config);
                SetItems(config);
                button.option('visible', true);
                button.option('disabled', false);
            } else {
                SW.Toastr.toastr_Error("Configuration Metadata not defined");
            }
        });

    function SetItems(x) {
        var items = [];
        x.columns.forEach(column => {
            if(!column.isConfigurable){
                return;
            }
            var item = {
                dataField: column.dataField,
                editorType: GetItemType(column.dataType),
                label: null
            }

            if(column.label != null && column.label != ""){
                item.label = {
                    text: column.label
                }
            }
            
            items.push(item);
        });
        var currentItems = e.component.option('items');
        var bla = currentItems.concat(items);
        e.component.option('items', bla);
    }

    function SetCompanyColumn(config){
        var item = {
            label: {
                text: "Company"
            },
            editorType: "dxSelectBox",
            name: "CompanySelectBox",
            editorOptions: {
                dataSource: {
                    load: function (e) { 
                        return SW.executeAPI('GET', 'skillviews/' + config.skillViewName).then(x => {
                            return x;
                        });
                    }
                },
                displayExpr: "Name",
                onValueChanged: function(e){
                    SW.getComponent('formIntegrator').option('formData', e.value)
                }
            }
        }
        var items = [item];
        e.component.option('items', items);
    }

    function GetItemType(x){
        switch (x) {
            case 0:
            case "Boolean":
                return 'dxCheckBox'
            case 1:
            case "Date":
                return 'dxDateBox'
            case 2:
            case "Float":
                return 'dxNumberBox'
            case 3:
            case 'Integer':
                return 'dxNumberBox'
            case 4:
            case "Money":
            case 5:
            case "Varchar50":
            case 6:
            case "Varchar100":
            case 7: 
            case "VarcharMax":
            case 8:
            case "Text":
            case 9: 
            case "UniqueIdentifier":
                return 'dxTextBox'

            default:
                return 'dxTextBox'
        }
    }
}"}
