    "onToolbarPreparing": "function(e) {
    var dataGrid = e.component;

    e.toolbarOptions.items.unshift({
        location: 'after',
        widget: 'dxButton',
        options: {
            icon: 'fal fa-times', onClick: function (e) {
                var userList = dataGrid.getSelectedRowKeys();
                if (userList.length != 1) {
                    SW.Toastr.toastr_Error('Select only 1 template');
                    return;
                };
                var templateSelected = dataGrid.getSelectedRowsData()[0];
                SW.executeAPI('DELETE', 'templates/' + templateSelected.Id)
                .then( x => {
                    SW.Toastr.toastr_Success("Deleted");
                });
            }
        }
    });

    e.toolbarOptions.items.unshift({
        location: 'after',
        widget: 'dxButton',
        options: {
            icon: 'plus', onClick: function () {
                var id = 'a8cbd629-35f8-49ec-8d58-5f0c36feae06';
                SkillsWorkflow.genericPopup('template', [{ Id:'a8cbd629-35f8-49ec-8d58-5f0c36feae06', Title:'New'}], null, 700, 800, false);
            }
        }
    });
}",
