{"onClick":"function myFunc(e){
    var buttonGroup = SW.getComponent('buttonGroup');
    var form = SW.getComponent('form');
    var grid = SW.getComponent('applyLeaveTypesGrid');
    var year = SW.getComponent('formLTyear');
    var days = SW.getComponent('formLTdays');
    var startDate = SW.getComponent('formLTstartDate');
    var expirationDate = SW.getComponent('formLTexpirationDate');
    var seniority = SW.getComponent('formLTseniority');
    var carryForward = SW.getComponent('formLTcarryForward');

    var gridUsers = SW.getComponent('data');
    var gridLeaveTypes = SW.getComponent('applyLeaveTypesGrid');
    if (gridUsers && gridLeaveTypes) {
        var selectedUsers = gridUsers.getSelectedRowKeys();
        var selectedLeaveTypes = gridLeaveTypes.getSelectedRowKeys();
    }
    if (selectedUsers.length == 0) {
        SW.editComponentsOptionInBulk([grid, form, buttonGroup, e.component], "disabled", false);
        return SW.Toastr.toastr_Error('No users selected');
    }
    if (selectedLeaveTypes.length == 0) {
        SW.editComponentsOptionInBulk([grid, form, buttonGroup, e.component], "disabled", false);
        return SW.Toastr.toastr_Error('No leave types selected');
    }
    var selectedUsersIds = [];
    selectedUsers.forEach(
        user => {
            selectedUsersIds.push(user.UserId);
        }
    );
    var selectedLeaveTypesIds = [];
    selectedLeaveTypes.forEach(
        leaveType => {
            selectedLeaveTypesIds.push(leaveType.Oid);
        }
    );

    var operation = buttonGroup.option('selectedItems')[0];
    if(operation.text == 'Apply'){
        Apply(e);
    } else {
        Delete(e);
    }

    function Apply(e) {
        SW.editComponentsOptionInBulk([grid, form, buttonGroup, e.component], "disabled", true);

        var yearValue = year != null ? year.option('value') : null;
        var daysValue = days != null ? days.option('value') : null;
        var startDateValue = startDate != null ? startDate.option('value') : null;
        var expirationDateValue = expirationDate != null ? expirationDate.option('value') : null;
        var seniorityValue = false;
        seniorityValue = seniority.option('value') != null ? seniority.option('value') : false;
        var carryForwardValue = false;
        carryForwardValue = carryForward.option('value') != null ? carryForward.option('value') : false;
        if(!daysValue){
            daysValue = 0;
        }
        if (!yearValue || !startDateValue || !expirationDateValue) {
            SW.editComponentsOptionInBulk([grid, form, buttonGroup, e.component], "disabled", false);
            return SW.Toastr.toastr_Error('A field is missing');
        }

        var details = {
            Year: yearValue,
            TotalVacationDays: daysValue,
            StartDate: startDateValue,
            ExpirationDate: expirationDateValue,
            Seniority: seniorityValue,
            CarryForward: carryForwardValue
        };
        var batchModel = {
            LeaveInfoBatchAction: 0,
            UsersIdList: selectedUsersIds,
            LeaveTypesIdList: selectedLeaveTypesIds,
            Details: details
        };
        SW.executeAPI('POST', 'v3/leave-infos/batch', null, batchModel).then(x => {
            SW.Toastr.toastr_Success('Applied');
            SW.editComponentsOptionInBulk([grid, form, buttonGroup, e.component], "disabled", false);
        }).catch(x => {
            SW.editComponentsOptionInBulk([grid, form, buttonGroup, e.component], "disabled", false);
        });
    }

    function Delete(e){
        SW.editComponentsOptionInBulk([grid, form, buttonGroup, e.component], "disabled", true);
        var batchModel = {
            LeaveInfoBatchAction: 1,
            UsersIdList: selectedUsersIds,
            LeaveTypesIdList: selectedLeaveTypesIds
        };
        SW.executeAPI('POST', 'v3/leave-infos/batch', null, batchModel).then(x => {
            SW.Toastr.toastr_Success('Deleted');
            SW.editComponentsOptionInBulk([grid, form, buttonGroup, e.component], "disabled", false);
            form.option('disabled', true);
        }).catch(x=>{
            SW.editComponentsOptionInBulk([grid, form, buttonGroup, e.component], "disabled", false);
            form.option('disabled', true);
        });
    }
}","onInitialized":"function f(e){
