{"onClick":"function f(e) {
    var buttonGroup = SW.getComponent('buttonGroup');
    var grid = SW.getComponent('applyMassLeaveTypesGrid');
    var form = SW.getComponent('formMLT');
    var year = SW.getComponent('formMLTyear');
    var halfDay = SW.getComponent('formMLThalfDay');
    var description = SW.getComponent('formMLTdescription');
    var untilDate = SW.getComponent('formMLTtoDate');
    var fromDate = SW.getComponent('formMLTfromDate');

    var gridUsers = SW.getComponent('data');
    var gridLeaveTypes = SW.getComponent('applyMassLeaveTypesGrid');
    if (gridUsers && gridLeaveTypes) {
        var selectedUsers = gridUsers.getSelectedRowKeys();
        var selectedLeaveTypes = gridLeaveTypes.getSelectedRowKeys();
    }
    if (selectedUsers.length == 0) {
        SW.editComponentsOptionInBulk([grid, form, buttonGroup, e.component], "disabled", false);
        return SW.Toastr.toastr_Error('No users selected');
    }
    if (selectedLeaveTypes.length == 0) {
        SW.editComponentsOptionInBulk([grid, form, buttonGroup, e.component], "disabled", false);
        return SW.Toastr.toastr_Error('No leave types selected');
    }
    var selectedUsersIds = [];
    selectedUsers.forEach(
        user => {
            selectedUsersIds.push(user.UserId);
        }
    );
    var selectedLeaveTypesIds = [];
    selectedLeaveTypes.forEach(
        leaveType => {
            selectedLeaveTypesIds.push(leaveType.Oid);
        }
    );

    var operation = buttonGroup.option('selectedItems')[0];
    if (operation.text == 'Apply') {
        Apply(e);
    } else {
        Delete(e);
    }

    function Apply(e){
        SW.editComponentsOptionInBulk([grid, form, buttonGroup, e.component], "disabled", true);
        
        var yearValue = year != null ? year.option('value') : null;
        var untilDateValue = untilDate != null ? untilDate.option('value') : null;
        var fromDateValue = fromDate != null ? fromDate.option('value') : null;
        var halfDayValue = false;
        halfDayValue = halfDay.option("value") != null ? halfDay.option('value') : false;
        var descriptionValue = description != null ? description.option('value') : null;
        if (!yearValue || !untilDateValue || !fromDateValue) {
            SW.editComponentsOptionInBulk([grid, form, buttonGroup, e.component], "disabled", false);
            return SW.Toastr.toastr_Error('A field is missing');
        }
        var details = {
            Year: yearValue,
            FromDate: fromDateValue,
            UntilDate: untilDateValue,
            HalfDay: halfDayValue,
            Description: descriptionValue
        };
        var batchModel = {
            LeaveInfoBatchAction: 0,
            UsersIdList: selectedUsersIds,
            LeaveTypesIdList: selectedLeaveTypesIds,
            ApplyDetails: details
        };
        SW.executeAPI('POST', 'v3/leave-periods/batch', null, batchModel).then(x => {
            SW.Toastr.toastr_Success('Applied');
            SW.editComponentsOptionInBulk([grid, form, buttonGroup, e.component], "disabled", false);
        }).catch(x => {
            SW.editComponentsOptionInBulk([grid, form, buttonGroup, e.component], "disabled", false);
        });
    }

    function Delete(e) {
        SW.editComponentsOptionInBulk([grid, form, buttonGroup, e.component], "disabled", true);
        
        var untilDateValue = untilDate != null ? untilDate.option('value') : null;
        var fromDateValue = fromDate != null ? fromDate.option('value') : null;
        if (!untilDate || !fromDate) {
            return SW.Toastr.toastr_Error('A field is missing');
        }
        var details = {
            FromDate: fromDateValue,
            UntilDate: untilDateValue,
        };
        var batchModel = {
            LeavePeriodBatchAction: 'DeleteMassLeaveType',
            UsersIdList: selectedUsersIds,
            LeaveTypesIdList: selectedLeaveTypesIds,
            DeleteDetails: details
        };
        SW.executeAPI('POST', 'v3/leave-periods/batch', null, batchModel)
        .then(x => {
            SW.Toastr.toastr_Success('Deleted');
            SW.editComponentsOptionInBulk([grid, form, buttonGroup, e.component], "disabled", false);
            SW.editComponentsOptionInBulk([year, halfDay, description], "disabled", true);
        }).catch(x=>{
            SW.editComponentsOptionInBulk([grid, form, buttonGroup, e.component], "disabled", false);
            SW.editComponentsOptionInBulk([year, halfDay, description], "disabled", true);
        });
    }
}","onInitialized":"function f(e){
    SW.saveComponent(e.component, "button");
}","disabled":true}
